function run(parameters):
  state = initialize(parameters)
  ENERGY_MODULUS = parameters.ENERGY_MODULUS > 0 ? parameters.ENERGY_MODULUS : 1

  function mod(a, n):
    return ((a % n) + n) % n

  for t from 1 to parameters.max_steps:
    groups = new Map()

    // 1. Identify and group active conductors
    for i from 0 to state.conductors.length - 1:
      conductor = state.conductors[i]
      activation_period = 1 + abs(i - conductor.target)
      if t % activation_period == 0:
        if not groups.has(conductor.target):
          groups.set(conductor.target, [])
        groups.get(conductor.target).push(i)

    // 2. Process groups in deterministic order
    sorted_targets = Array.from(groups.keys()).sort((a, b) => a - b)

    for target_idx in sorted_targets:
      group_ids = groups.get(target_idx) // Already sorted asc by insertion order of i
      if group_ids.length < 2:
        continue

      leader_idx = group_ids[0]
      leader_charge_at_start = state.conductors[leader_idx].charge

      for j from 1 to group_ids.length - 1:
        member_idx = group_ids[j]

        // 3. Apply update rule
        leader_current_charge = state.conductors[leader_idx].charge
        member_current_charge = state.conductors[member_idx].charge

        state.conductors[leader_idx].charge = mod(leader_current_charge + member_current_charge, ENERGY_MODULUS)
        state.conductors[member_idx].charge = mod(leader_charge_at_start, ENERGY_MODULUS)

        state.conductors[leader_idx].last_sync_step = t
        state.conductors[member_idx].last_sync_step = t

  return { conductors: state.conductors }

