class Simulation {
  constructor() {
    this.state = null;
  }

  _mod(a, n) {
    return ((a % n) + n) % n;
  }

  _initialize(params) {
    const { num_conductors, initial_setup } = params;
    this.state = { conductors: [] };
    for (let i = 0; i < num_conductors; i++) {
      this.state.conductors.push({
        id: i,
        charge: initial_setup[i].charge,
        target: initial_setup[i].target,
        last_sync_step: 0,
      });
    }
  }

  run(parameters) {
    const { max_steps } = parameters;
    const ENERGY_MODULUS = parameters.ENERGY_MODULUS > 0 ? parameters.ENERGY_MODULUS : 1;
    this._initialize(parameters);

    for (let t = 1; t <= max_steps; t++) {
      const groups = new Map();

      for (let i = 0; i < this.state.conductors.length; i++) {
        const conductor = this.state.conductors[i];
        const activation_period = 1 + Math.abs(i - conductor.target);
        if (activation_period > 0 && t % activation_period === 0) {
          if (!groups.has(conductor.target)) {
            groups.set(conductor.target, []);
          }
          groups.get(conductor.target).push(i);
        }
      }

      const sorted_targets = Array.from(groups.keys()).sort((a, b) => a - b);

      for (const target_idx of sorted_targets) {
        const group_ids = groups.get(target_idx);
        if (group_ids.length < 2) {
          continue;
        }

        const leader_idx = group_ids[0];
        const leader_charge_at_start = this.state.conductors[leader_idx].charge;

        for (let j = 1; j < group_ids.length; j++) {
          const member_idx = group_ids[j];

          const leader_current_charge = this.state.conductors[leader_idx].charge;
          const member_current_charge = this.state.conductors[member_idx].charge;

          this.state.conductors[leader_idx].charge = this._mod(leader_current_charge + member_current_charge, ENERGY_MODULUS);
          this.state.conductors[member_idx].charge = this._mod(leader_charge_at_start, ENERGY_MODULUS);

          this.state.conductors[leader_idx].last_sync_step = t;
          this.state.conductors[member_idx].last_sync_step = t;
        }
      }
    }

    return JSON.parse(JSON.stringify(this.state));
  }
}

