// VALIDATOR IMPLEMENTATION FOR: Chrono-Sync Network

class Simulation {
  constructor() {
    // No global mutable state is stored between runs
  }

  // Helper: true mathematical modulus
  _mod(a, n) {
    return ((a % n) + n) % n;
  }

  run(parameters) {
    // Extract and prepare parameters with defensive defaults
    const num_conductors = typeof parameters.num_conductors === 'number'
      ? parameters.num_conductors
      : 0;

    const initial_setup = Array.isArray(parameters.initial_setup)
      ? parameters.initial_setup
      : [];

    const max_steps = typeof parameters.max_steps === 'number'
      ? parameters.max_steps
      : 0;

    const ENERGY_MODULUS = (typeof parameters.ENERGY_MODULUS === 'number' && parameters.ENERGY_MODULUS > 0)
      ? parameters.ENERGY_MODULUS
      : 1;

    // Initialise conductors array
    const conductors = [];
    for (let i = 0; i < num_conductors; i++) {
      const initData = initial_setup[i] || {};
      conductors.push({
        id: i,
        charge: typeof initData.charge === 'number' ? initData.charge : 0,
        target: typeof initData.target === 'number' ? initData.target : i,
        last_sync_step: 0
      });
    }

    // Early termination if no steps required
    if (max_steps === 0 || num_conductors === 0) {
      return { conductors };
    }

    // Main simulation loop
    for (let t = 1; t <= max_steps; t++) {
      // Map: target index -> array of active conductor ids
      const groups = new Map();

      // 1. Identify & group active conductors
      for (let i = 0; i < num_conductors; i++) {
        const conductor = conductors[i];
        const activation_period = 1 + Math.abs(i - conductor.target);
        if (t % activation_period === 0) {
          if (!groups.has(conductor.target)) {
            groups.set(conductor.target, []);
          }
          groups.get(conductor.target).push(i);
        }
      }

      // 2. Ensure each group's IDs are sorted ascending
      for (const ids of groups.values()) {
        ids.sort((a, b) => a - b);
      }

      // 3. Process groups in ascending order of target index
      const sorted_targets = Array.from(groups.keys()).sort((a, b) => a - b);

      for (const target_idx of sorted_targets) {
        const group_ids = groups.get(target_idx);
        if (group_ids.length < 2) {
          continue; // No synchronization for singleton groups
        }

        const leader_idx = group_ids[0];
        const leader_start_charge = conductors[leader_idx].charge;

        // 4. Synchronize members with leader
        for (let j = 1; j < group_ids.length; j++) {
          const member_idx = group_ids[j];

          const leader_current_charge = conductors[leader_idx].charge;
          const member_current_charge = conductors[member_idx].charge;

          // Update leader charge
          conductors[leader_idx].charge = this._mod(
            leader_current_charge + member_current_charge,
            ENERGY_MODULUS
          );

          // Update member charge
          conductors[member_idx].charge = this._mod(
            leader_start_charge,
            ENERGY_MODULUS
          );

          // Update last_sync_step for both
          conductors[leader_idx].last_sync_step = t;
          conductors[member_idx].last_sync_step = t;
        }
      }
    }

    return { conductors };
  }
}
