class Simulation {
  constructor() {}

  run(parameters) {
    const {size, modulus, c1, c2, initial_grid, max_steps} = parameters;

    let grid = initial_grid.map(row => [...row]);
    const n = size;
    const p = modulus;

    for (let step = 0; step < max_steps; step++) {
      const new_grid = Array(n).fill().map(() => Array(n).fill(0));

      for (let i = 0; i < n; i++) {
        for (let j = 0; j < n; j++) {
          const up = grid[(i - 1 + n) % n][j];
          const down = grid[(i + 1) % n][j];
          const left = grid[i][(j - 1 + n) % n];
          const right = grid[i][(j + 1) % n];

          const neighbor_sum = up + down + left + right;
          new_grid[i][j] = (neighbor_sum + c1 * grid[i][j] + c2) % p;
        }
      }

      grid = new_grid;
    }

    let total_sum = 0;
    let nonzero_count = 0;
    let max_value = 0;

    for (let i = 0; i < n; i++) {
      for (let j = 0; j < n; j++) {
        total_sum += grid[i][j];
        if (grid[i][j] !== 0) nonzero_count++;
        if (grid[i][j] > max_value) max_value = grid[i][j];
      }
    }

    const center_i = Math.floor(n / 2);
    const center_j = Math.floor(n / 2);
    const center_value = grid[center_i][center_j];

    return {
      grid: grid,
      total_sum: total_sum,
      nonzero_count: nonzero_count,
      max_value: max_value,
      center_value: center_value
    };
  }
}

