// VALIDATOR IMPLEMENTATION FOR: Prime Field Cellular Evolution

class Simulation {
  constructor() {
    // No internal mutable state required across runs
  }

  // Deep copy utility for a 2-D array
  _copyGrid(grid) {
    const size = grid.length;
    const copy = new Array(size);
    for (let i = 0; i < size; i++) {
      copy[i] = grid[i].slice(); // shallow copy of each row (rows are 1-D arrays)
    }
    return copy;
  }

  run(parameters) {
    // Extract parameters
    const size        = parameters.size;
    const modulus     = parameters.modulus;
    const c1          = parameters.c1;
    const c2          = parameters.c2;
    const initialGrid = parameters.initial_grid;
    const maxSteps    = parameters.max_steps;

    // Copy initial grid to avoid mutating the input
    let grid = this._copyGrid(initialGrid);

    // Main simulation loop
    for (let step = 0; step < maxSteps; step++) {
      const newGrid = new Array(size);
      for (let i = 0; i < size; i++) {
        const row = new Array(size);
        for (let j = 0; j < size; j++) {
          // Toroidal neighbor indices
          const up    = (i - 1 + size) % size;
          const down  = (i + 1) % size;
          const left  = (j - 1 + size) % size;
          const right = (j + 1) % size;

          // Sum of orthogonal neighbors
          const neighborSum = grid[up][j] + grid[down][j] + grid[i][left] + grid[i][right];

          // Update rule modulo prime modulus
          const newValue = (neighborSum + c1 * grid[i][j] + c2) % modulus;

          row[j] = newValue;
        }
        newGrid[i] = row;
      }
      grid = newGrid; // simultaneous update
    }

    // Compute required statistics
    let totalSum = 0;
    let nonzeroCount = 0;
    let maxValue = 0;

    for (let i = 0; i < size; i++) {
      const row = grid[i];
      for (let j = 0; j < size; j++) {
        const val = row[j];
        totalSum += val;
        if (val !== 0) nonzeroCount++;
        if (val > maxValue) maxValue = val;
      }
    }

    const centerIndex = Math.floor(size / 2);
    const centerValue = grid[centerIndex][centerIndex];

    // Assemble result object matching the return schema
    const result = {
      grid: grid,
      total_sum: totalSum,
      nonzero_count: nonzeroCount,
      max_value: maxValue,
      center_value: centerValue
    };

    return result;
  }
}
