// VALIDATOR IMPLEMENTATION FOR: Resonance Cascade

class Simulation {
  constructor() {
    // No external state required
  }

  run(parameters) {
    const {
      initial_frequencies,
      initial_phases,
      catalyst_index,
      catalyst_factor,
      MOD_PHASE,
      MOD_FREQ,
      max_steps
    } = parameters;

    const count = initial_frequencies.length;
    const resonators = new Array(count);

    // Initialize resonators
    for (let i = 0; i < count; i++) {
      resonators[i] = {
        frequency: initial_frequencies[i],
        phase: initial_phases[i]
      };
    }

    const catalystIdx = typeof catalyst_index === 'number' ? catalyst_index : -1;

    // Main simulation loop
    for (let step = 1; step <= max_steps; step++) {
      const acted_this_step = new Array(count).fill(false);

      // Phase update (simultaneous)
      const new_phases = new Array(count);
      for (let i = 0; i < count; i++) {
        new_phases[i] = (resonators[i].phase + resonators[i].frequency) % MOD_PHASE;
      }
      for (let i = 0; i < count; i++) {
        resonators[i].phase = new_phases[i];
      }

      // Interaction step
      for (let i = 0; i < count; i++) {
        if (acted_this_step[i]) continue;

        const neighbors = [];
        if (i > 0) neighbors.push(i - 1);
        if (i < count - 1) neighbors.push(i + 1);

        for (let k = 0; k < neighbors.length; k++) {
          const j = neighbors[k];
          if (acted_this_step[j]) continue;

          const p_i = resonators[i].phase;
          const p_j = resonators[j].phase;

          const is_resonance =
            p_i > 0 &&
            p_j > 0 &&
            p_i !== p_j &&
            (p_i % p_j === 0 || p_j % p_i === 0);

          if (is_resonance) {
            const catalystInteraction = i === catalystIdx || j === catalystIdx;

            if (catalystInteraction) {
              const target = i === catalystIdx ? j : i;
              let newFreq = (resonators[target].frequency * catalyst_factor) % MOD_FREQ;
              if (newFreq === 0) newFreq = 1;
              resonators[target].frequency = newFreq;
            } else {
              const tempFreq = resonators[i].frequency;
              const tempPhase = resonators[i].phase;

              resonators[i].frequency = resonators[j].frequency;
              resonators[i].phase = resonators[j].phase;

              resonators[j].frequency = tempFreq;
              resonators[j].phase = tempPhase;
            }

            acted_this_step[i] = true;
            acted_this_step[j] = true;
            break; // i has interacted; move to next i
          }
        }
      }
    }

    // Prepare result
    const resultResonators = resonators.map(r => ({
      frequency: r.frequency,
      phase: r.phase
    }));

    return {
      step: max_steps,
      resonators: resultResonators
    };
  }
}
