function run(parameters):
  width = parameters.width
  height = parameters.height
  max_steps = parameters.max_steps
  comets = initialize comets from parameters.bots, adding an 'id' to each
  collision_count = 0

  DELTAS = {N:{dx:0,dy:-1}, E:{dx:1,dy:0}, S:{dx:0,dy:1}, W:{-1,dy:0}}

  for step from 0 to max_steps-1:
    // 1. Movement Phase
    next_positions = map of (y*width+x) -> list of comet_ids
    for each comet_id from 0 to comets.length-1:
      comet = comets[comet_id]
      delta = DELTAS[comet.dir]
      nx = (comet.x + delta.dx + width) % width
      ny = (comet.y + delta.dy + height) % height
      key = ny * width + nx
      if key not in next_positions: next_positions[key] = []
      add comet_id to next_positions[key]

    // 2. Collision Phase
    collision_coords = []
    for key, ids in next_positions:
      if ids.length > 1:
        y = floor(key / width)
        x = key % width
        add {x, y, ids} to collision_coords

    // Sort collisions by coordinate
    sort collision_coords by y, then by x

    pending_updates = map of comet_id -> {new_dir, new_type}

    for each collision in collision_coords:
      collision_count++
      colliding_ids = collision.ids // These are already sorted as they were added in id order
      first_comet_id = colliding_ids[0]
      first_comet = comets[first_comet_id]

      if first_comet.type == 'P':
        // First comet rotates, others reverse
        pending_updates[first_comet_id] = { new_dir: ROTATE_CW[first_comet.dir] }
        for other_id in colliding_ids from index 1 to end:
          other_comet = comets[other_id]
          pending_updates[other_id] = { new_dir: REVERSE[other_comet.dir] }
      else: // first_comet.type == 'R'
        // First comet reverses, others rotate
        pending_updates[first_comet_id] = { new_dir: REVERSE[first_comet.dir] }
        for other_id in colliding_ids from index 1 to end:
          other_comet = comets[other_id]
          pending_updates[other_id] = { new_dir: ROTATE_CW[other_comet.dir] }

      // All comets flip type
      for id in colliding_ids:
        if id not in pending_updates: pending_updates[id] = {}
        pending_updates[id].new_type = comets[id].type == 'P' ? 'R' : 'P'

    // 3. Update Phase
    for comet_id from 0 to comets.length-1:
        key = find key in next_positions that contains comet_id
        comets[comet_id].y = floor(key / width)
        comets[comet_id].x = key % width
        if comet_id in pending_updates:
          update = pending_updates[comet_id]
          if update.new_dir is not null: comets[comet_id].dir = update.new_dir
          if update.new_type is not null: comets[comet_id].type = update.new_type

  return { final_bots: comets, collision_count: collision_count }

