class Simulation {
    constructor() {}

    run(parameters) {
        const { width, height, max_steps, bots: initialBots } = parameters;
        let comets = initialBots.map((bot, i) => ({ ...bot, id: i }));
        let collision_count = 0;

        const DELTAS = { N: { dx: 0, dy: -1 }, E: { dx: 1, dy: 0 }, S: { dx: 0, dy: 1 }, W: { dx: -1, dy: 0 } };
        const ROTATE_CW = { N: 'E', E: 'S', S: 'W', W: 'N' };
        const REVERSE = { N: 'S', E: 'W', S: 'N', W: 'E' };

        for (let step = 0; step < max_steps; step++) {
            if (comets.length === 0) break;

            const next_positions = new Map();
            const comet_new_coords = new Array(comets.length);

            for (let i = 0; i < comets.length; i++) {
                const comet = comets[i];
                const delta = DELTAS[comet.dir];
                const nx = (comet.x + delta.dx + width) % width;
                const ny = (comet.y + delta.dy + height) % height;
                const key = ny * width + nx;
                comet_new_coords[i] = { nx, ny };
                if (!next_positions.has(key)) {
                    next_positions.set(key, []);
                }
                next_positions.get(key).push(comet.id);
            }

            const collision_coords = [];
            for (const [key, ids] of next_positions.entries()) {
                if (ids.length > 1) {
                    collision_coords.push({ key, ids });
                }
            }

            collision_coords.sort((a, b) => a.key - b.key);

            const pending_updates = new Map();

            for (const collision of collision_coords) {
                collision_count++;
                const colliding_ids = collision.ids; // Already sorted by id due to insertion order
                const first_comet_id = colliding_ids[0];
                const first_comet = comets[first_comet_id];

                if (first_comet.type === 'P') {
                    pending_updates.set(first_comet_id, { new_dir: ROTATE_CW[first_comet.dir] });
                    for (let i = 1; i < colliding_ids.length; i++) {
                        const other_id = colliding_ids[i];
                        pending_updates.set(other_id, { new_dir: REVERSE[comets[other_id].dir] });
                    }
                } else { // type === 'R'
                    pending_updates.set(first_comet_id, { new_dir: REVERSE[first_comet.dir] });
                    for (let i = 1; i < colliding_ids.length; i++) {
                        const other_id = colliding_ids[i];
                        pending_updates.set(other_id, { new_dir: ROTATE_CW[comets[other_id].dir] });
                    }
                }

                for (const id of colliding_ids) {
                    const new_type = comets[id].type === 'P' ? 'R' : 'P';
                    if (!pending_updates.has(id)) pending_updates.set(id, {});
                    pending_updates.get(id).new_type = new_type;
                }
            }

            const next_comets = new Array(comets.length);
            for (let i = 0; i < comets.length; i++) {
                const comet = comets[i];
                const { nx, ny } = comet_new_coords[i];
                const update = pending_updates.get(comet.id);

                next_comets[i] = {
                    id: comet.id,
                    x: nx,
                    y: ny,
                    dir: update?.new_dir ?? comet.dir,
                    type: update?.new_type ?? comet.type,
                };
            }
            comets = next_comets;
        }

        return {
            final_bots: comets.sort((a,b) => a.id - b.id),
            collision_count
        };
    }
}

