// VALIDATOR IMPLEMENTATION FOR: Colliding Comets

class Simulation {
  constructor() {
    // No global state
  }

  // Helper maps
  static get DELTAS() {
    return {
      N: { dx: 0, dy: -1 },
      E: { dx: 1, dy: 0 },
      S: { dx: 0, dy: 1 },
      W: { dx: -1, dy: 0 }
    };
  }

  static get ROTATE_CW() {
    return { N: 'E', E: 'S', S: 'W', W: 'N' };
  }

  static get REVERSE() {
    return { N: 'S', S: 'N', E: 'W', W: 'E' };
  }

  run(parameters) {
    const width = parameters.width >>> 0;
    const height = parameters.height >>> 0;
    const max_steps = parameters.max_steps >>> 0;
    const bots = Array.isArray(parameters.bots) ? parameters.bots : [];

    // Initialize comets with ids
    const comets = [];
    for (let i = 0; i < bots.length; i++) {
      const b = bots[i];
      comets.push({
        id: i,
        x: b.x >>> 0,
        y: b.y >>> 0,
        dir: b.dir,
        type: b.type
      });
    }

    let collision_count = 0;

    // Early exit if no steps
    if (max_steps === 0) {
      return { final_bots: comets, collision_count };
    }

    // Preallocate arrays for next positions
    const nextX = new Array(comets.length);
    const nextY = new Array(comets.length);

    for (let step = 0; step < max_steps; step++) {
      // 1. Movement Phase
      const nextPositions = Object.create(null); // key -> array of ids

      for (let id = 0; id < comets.length; id++) {
        const comet = comets[id];
        const delta = Simulation.DELTAS[comet.dir];
        const nx = (comet.x + delta.dx + width) % width;
        const ny = (comet.y + delta.dy + height) % height;
        nextX[id] = nx;
        nextY[id] = ny;
        const key = ny * width + nx;
        if (!nextPositions[key]) {
          nextPositions[key] = [];
        }
        nextPositions[key].push(id); // ids pushed in ascending order
      }

      // 2. Collision Phase
      const collisionCoords = [];
      for (const key in nextPositions) {
        const ids = nextPositions[key];
        if (ids.length > 1) {
          const k = +key;
          const y = Math.floor(k / width);
          const x = k % width;
          collisionCoords.push({ x, y, ids });
        }
      }

      // Sort collisions row-major (y, then x)
      collisionCoords.sort((a, b) => (a.y - b.y) || (a.x - b.x));

      const pending_updates = Object.create(null); // id -> {new_dir?, new_type?}

      for (let c = 0; c < collisionCoords.length; c++) {
        collision_count++;
        const colliding_ids = collisionCoords[c].ids; // already sorted asc
        const first_id = colliding_ids[0];
        const first_comet = comets[first_id];
        const first_type = first_comet.type;

        if (first_type === 'P') {
          // First rotates clockwise
          pending_updates[first_id] = {
            new_dir: Simulation.ROTATE_CW[first_comet.dir]
          };
          // Others reverse
          for (let j = 1; j < colliding_ids.length; j++) {
            const oid = colliding_ids[j];
            const ocomet = comets[oid];
            pending_updates[oid] = {
              new_dir: Simulation.REVERSE[ocomet.dir]
            };
          }
        } else { // 'R'
          // First reverses
          pending_updates[first_id] = {
            new_dir: Simulation.REVERSE[first_comet.dir]
          };
          // Others rotate clockwise
          for (let j = 1; j < colliding_ids.length; j++) {
            const oid = colliding_ids[j];
            const ocomet = comets[oid];
            pending_updates[oid] = {
              new_dir: Simulation.ROTATE_CW[ocomet.dir]
            };
          }
        }

        // All flip type
        for (let j = 0; j < colliding_ids.length; j++) {
          const cid = colliding_ids[j];
          if (!pending_updates[cid]) pending_updates[cid] = {};
          pending_updates[cid].new_type = comets[cid].type === 'P' ? 'R' : 'P';
        }
      }

      // 3. Update Phase
      for (let id = 0; id < comets.length; id++) {
        comets[id].x = nextX[id];
        comets[id].y = nextY[id];
        const upd = pending_updates[id];
        if (upd) {
          if (upd.new_dir !== undefined) comets[id].dir = upd.new_dir;
          if (upd.new_type !== undefined) comets[id].type = upd.new_type;
        }
      }
    }

    return { final_bots: comets, collision_count };
  }
}
