    // Initialize grid to all zeros
    let grid = Array(grid_size).fill(null).map(() => Array(grid_size).fill(0));

    // Set initial pattern with bounds checking
    for (const cell of initial_pattern) {
      if (cell.x >= 0 && cell.x < grid_size && cell.y >= 0 && cell.y < grid_size) {
        grid[cell.x][cell.y] = cell.species;
      }
    }

    // Evolution loop
    for (let step = 0; step < max_steps; step++) {
      grid = this._evolveGrid(grid, grid_size);
    }

    // Return final state
    return {
      final_grid: grid,
      species_counts: this._countSpecies(grid),
      total_population: this._getTotalPopulation(grid),
      center_species: grid[Math.floor(grid_size/2)][Math.floor(grid_size/2)]
    };
  }

  _evolveGrid(grid, size) {
    const new_grid = Array(size).fill(null).map(() => Array(size).fill(0));

    for (let x = 0; x < size; x++) {
      for (let y = 0; y < size; y++) {
        const neighbors = this._getNeighborCounts(grid, x, y, size);
        new_grid[x][y] = this._getNextSpecies(grid[x][y], neighbors);
      }
    }

    return new_grid;
  }

  _getNeighborCounts(grid, x, y, size) {
    const counts = [0, 0, 0, 0]; // counts for species 0, 1, 2, 3

    // Scan in lexicographic order
    for (let dx = -1; dx <= 1; dx++) {
      for (let dy = -1; dy <= 1; dy++) {
        if (dx === 0 && dy === 0) continue;

        const nx = (x + dx + size) % size;
        const ny = (y + dy + size) % size;
        counts[grid[nx][ny]]++;
      }
    }

    return counts;
  }

  _getNextSpecies(current, neighbors) {
    const total_neighbors = neighbors.reduce((a, b) => a + b, 0);

    switch(current) {
      case 0: // Empty space - birth rules in species order
        if (neighbors[1] === 3) return 1;
        if (neighbors[2] === 3) return 2;  
        if (neighbors[3] === 2) return 3;
        return 0;

      case 1: // Species 1 (stable, Conway-like)
        if (neighbors[1] === 2 || neighbors[1] === 3) return 1;
        return 0;

      case 2: // Species 2 (aggressive)
        if (total_neighbors >= 2 && total_neighbors <= 5) return 2;
        return 0;

      case 3: // Species 3 (defensive)
        if (neighbors[3] >= 1 && total_neighbors <= 6) return 3;
        return 0;

      default:
        return 0;
    }
  }

  _countSpecies(grid) {
    const counts = [0, 0, 0, 0];
    for (let x = 0; x < grid.length; x++) {
      for (let y = 0; y < grid[x].length; y++) {
        counts[grid[x][y]]++;
      }
    }
    return counts;
  }

  _getTotalPopulation(grid) {
    let total = 0;
    for (let x = 0; x < grid.length; x++) {
      for (let y = 0; y < grid[x].length; y++) {
        if (grid[x][y] > 0) total++;
      }
    }
    return total;
  }
