// VALIDATOR IMPLEMENTATION FOR: Multi-Species Cellular Evolution

class Simulation {
  constructor() {
    // No internal persistent state is required.
  }

  // Count neighbors for cell (x, y) on a toroidal grid.
  _countNeighbors(grid, x, y, size) {
    const counts = [0, 0, 0, 0]; // indices 0..3 for species
    // Lexicographic order over (dx, dy)
    for (let dx = -1; dx <= 1; dx++) {
      for (let dy = -1; dy <= 1; dy++) {
        if (dx === 0 && dy === 0) continue;
        const nx = (x + dx + size) % size;
        const ny = (y + dy + size) % size;
        counts[grid[nx][ny]]++;
      }
    }
    return counts;
  }

  // Determine next species for a single cell based on current state and neighbor counts.
  _nextSpecies(current, neighbors) {
    const total = neighbors[0] + neighbors[1] + neighbors[2] + neighbors[3];
    if (current === 0) {
      // Birth rules with tie-breaker order 1, then 2, then 3
      if (neighbors[1] === 3) return 1;
      if (neighbors[2] === 3) return 2;
      if (neighbors[3] === 2) return 3;
      return 0;
    } else if (current === 1) {
      return (neighbors[1] === 2 || neighbors[1] === 3) ? 1 : 0;
    } else if (current === 2) {
      return (total >= 2 && total <= 5) ? 2 : 0;
    } else { // current === 3
      return (neighbors[3] >= 1 && total <= 6) ? 3 : 0;
    }
  }

  run(parameters) {
    // Extract parameters with defaults.
    const size = parameters.grid_size;
    const maxSteps = parameters.max_steps;
    const initialPattern = Array.isArray(parameters.initial_pattern) ? parameters.initial_pattern : [];

    // Initialize grid with zeros.
    const grid = Array.from({ length: size }, () => Array(size).fill(0));

    // Apply initial pattern with bounds checking.
    for (let i = 0; i < initialPattern.length; i++) {
      const cell = initialPattern[i];
      const x = cell.x;
      const y = cell.y;
      const s = cell.species;
      if (
        Number.isInteger(x) && Number.isInteger(y) &&
        x >= 0 && x < size && y >= 0 && y < size &&
        (s === 1 || s === 2 || s === 3)
      ) {
        grid[x][y] = s;
      }
    }

    // Evolution loop.
    let currentGrid = grid;
    for (let step = 0; step < maxSteps; step++) {
      const newGrid = Array.from({ length: size }, () => Array(size).fill(0));
      for (let x = 0; x < size; x++) {
        for (let y = 0; y < size; y++) {
          const neighbors = this._countNeighbors(currentGrid, x, y, size);
          newGrid[x][y] = this._nextSpecies(currentGrid[x][y], neighbors);
        }
      }
      currentGrid = newGrid;
    }

    // Compute statistics.
    const speciesCounts = [0, 0, 0, 0];
    for (let x = 0; x < size; x++) {
      for (let y = 0; y < size; y++) {
        speciesCounts[currentGrid[x][y]]++;
      }
    }
    const totalPopulation = speciesCounts[1] + speciesCounts[2] + speciesCounts[3];
    const center = Math.floor(size / 2);
    const centerSpecies = currentGrid[center][center];

    // Prepare result.
    return {
      final_grid: currentGrid,
      species_counts: speciesCounts,
      total_population: totalPopulation,
      center_species: centerSpecies
    };
  }
}
