class Simulation {
    constructor() {
        this.L = 0;
        this.M = 0;
        this.walkers = [];
        this.field = [];
    }

    _init(params) {
        this.L = params.L;
        this.M = params.M;

        this.walkers = params.initial_walkers.map((w, i) => ({
            id: i,
            pos: w.pos,
            dir: w.dir,
        }));

        if (params.initial_field && params.initial_field.length === this.L) {
            this.field = [...params.initial_field];
        } else {
            this.field = Array(this.L).fill(0);
        }
    }

    run(parameters) {
        this._init(parameters);
        const max_steps = parameters.max_steps;

        for (let step = 0; step < max_steps; step++) {
            const field_updates = new Map();

            // Walker processing phase, in ascending id order
            for (const walker of this.walkers) {
                const current_pos = walker.pos;
                const field_val = this.field[current_pos];

                // Direction update rule
                if (field_val > 0 && field_val % 2 === 0) {
                    walker.dir *= -1;
                }

                // Schedule field update
                const new_field_val = (field_val + 1) % this.M;
                field_updates.set(current_pos, new_field_val);

                // Movement
                walker.pos += walker.dir;
            }

            // Apply field updates. Map correctly handles multiple walkers
            // on the same cell - last one (highest id) wins.
            for (const [pos, val] of field_updates) {
                this.field[pos] = val;
            }

            // Boundary reflection phase, in ascending id order
            for (const walker of this.walkers) {
                if (walker.pos < 0) {
                    walker.pos = 0;
                    walker.dir = 1;
                } else if (walker.pos >= this.L) {
                    walker.pos = this.L - 1;
                    walker.dir = -1;
                }
            }
        }

        const final_walkers_serializable = this.walkers.map(w => ({...w}));

        return {
            final_walkers: final_walkers_serializable,
            final_field: [...this.field]
        };
    }
}

