  _cloneWalkers(walkers) {
    const out = new Array(walkers.length);
    for (let i = 0; i < walkers.length; i++) {
      const w = walkers[i];
      out[i] = { id: w.id, pos: w.pos, dir: w.dir };
    }
    return out;
  }

  run(parameters) {
    const L = parameters.L;
    const M = parameters.M;
    const initial_walkers = parameters.initial_walkers || [];
    const max_steps = parameters.max_steps || 0;
    const initial_field = parameters.initial_field;

    /* --- Initialisation --- */
    // Initialise walkers array, ordered by id (ascending)
    const walkers = new Array(initial_walkers.length);
    for (let i = 0; i < initial_walkers.length; i++) {
      const w = initial_walkers[i];
      walkers[i] = { id: i, pos: w.pos, dir: w.dir };
    }

    // Initialise field array
    let field;
    if (Array.isArray(initial_field)) {
      field = initial_field.slice(0, L);
      while (field.length < L) field.push(0);
    } else {
      field = new Array(L).fill(0);
    }

    /* --- Main Simulation Loop --- */
    for (let step = 0; step < max_steps; step++) {
      // Field updates scheduled during this step
      const fieldUpdates = Object.create(null); // key: position, value: new field value

      /* Walker Processing Phase */
      for (let i = 0; i < walkers.length; i++) {
        const walker = walkers[i];
        const currentPos = walker.pos;
        const fieldVal = field[currentPos];

        // Direction update rule
        if (fieldVal > 0 && fieldVal % 2 === 0) {
          walker.dir = -walker.dir;
        }

        // Schedule field modification (later walkers override earlier ones)
        const newFieldVal = (fieldVal + 1) % M;
        fieldUpdates[currentPos] = newFieldVal;

        // Movement
        walker.pos = walker.pos + walker.dir;
      }

      /* Field Update Phase */
      for (const posStr in fieldUpdates) {
        const pos = Number(posStr);
        field[pos] = fieldUpdates[posStr];
      }

      /* Boundary Reflection Phase */
      for (let i = 0; i < walkers.length; i++) {
        const walker = walkers[i];
        if (walker.pos < 0) {
          walker.pos = 0;
          walker.dir = 1;
        } else if (walker.pos >= L) {
          walker.pos = L - 1;
          walker.dir = -1;
        }
      }
    }

    /* --- Return Final State --- */
    return {
      final_walkers: this._cloneWalkers(walkers),
      final_field: field.slice()
    };
  }
}
