class Simulation {
  constructor() {}

  run(parameters) {
    const { track_size, walkers, max_steps } = parameters;

    // Copy walkers to avoid modifying input
    let current_walkers = walkers.map(w => ({
      id: w.id,
      position: w.position,
      velocity: w.velocity
    }));

    for (let step = 0; step < max_steps; step++) {
      // Phase 1: Move all walkers
      for (let walker of current_walkers) {
        walker.position = (walker.position + walker.velocity) % track_size;
      }

      // Phase 2: Group walkers by position
      const position_groups = {};
      for (let walker of current_walkers) {
        const pos = walker.position;
        if (!(pos in position_groups)) {
          position_groups[pos] = [];
        }
        position_groups[pos].push(walker);
      }

      // Phase 3: Handle collisions (velocity exchanges)
      for (let pos in position_groups) {
        const group = position_groups[pos];
        if (group.length > 1) {
          // Sort by ID for deterministic collision resolution
          group.sort((a, b) => a.id - b.id);

          // Circular velocity exchange
          const velocities = group.map(w => w.velocity);
          for (let i = 0; i < group.length; i++) {
            group[i].velocity = velocities[(i + 1) % group.length];
          }
        }
      }
    }

    // Calculate final statistics
    const position_counts = new Array(track_size).fill(0);
    for (let walker of current_walkers) {
      position_counts[walker.position]++;
    }

    const velocities = current_walkers.map(w => w.velocity);
    const total_velocity = velocities.reduce((sum, v) => sum + v, 0);
    const max_velocity = Math.max(...velocities);
    const min_velocity = Math.min(...velocities);
    const occupied_positions = position_counts.filter(count => count > 0).length;

    return {
      walkers: current_walkers.sort((a, b) => a.id - b.id),
      position_counts: position_counts,
      total_velocity: total_velocity,
      max_velocity: max_velocity,
      min_velocity: min_velocity,
      occupied_positions: occupied_positions
    };
  }
