// VALIDATOR IMPLEMENTATION FOR: Circular Track Walker System

class Simulation {
  constructor() {
    // No internal mutable state needed between runs
  }

  // Helper for positive modular arithmetic
  _mod(n, m) {
    return ((n % m) + m) % m;
  }

  // Deep copy a walker object
  _copyWalker(w) {
    return { id: w.id, position: w.position, velocity: w.velocity };
  }

  run(parameters) {
    // Extract parameters with default fallbacks
    const trackSize = parameters.track_size;
    const maxSteps = parameters.max_steps || 0;
    const inputWalkers = Array.isArray(parameters.walkers) ? parameters.walkers : [];

    // Edge case: trackSize must be positive to perform mod operations
    if (trackSize <= 0) {
      throw new Error("track_size must be a positive integer");
    }

    // Deep copy walkers to avoid mutating caller data
    const currentWalkers = inputWalkers.map(w => this._copyWalker(w));

    // Main simulation loop
    for (let step = 0; step < maxSteps; step++) {
      // Move phase
      for (let i = 0; i < currentWalkers.length; i++) {
        const walker = currentWalkers[i];
        walker.position = this._mod(walker.position + walker.velocity, trackSize);
      }

      // Collision phase
      const groups = new Map(); // position -> array of walkers
      for (let i = 0; i < currentWalkers.length; i++) {
        const walker = currentWalkers[i];
        const pos = walker.position;
        if (!groups.has(pos)) groups.set(pos, []);
        groups.get(pos).push(walker);
      }

      // Process each collision group
      for (const [pos, group] of groups) {
        if (group.length > 1) {
          // Sort by id ascending
          group.sort((a, b) => a.id - b.id);

          // Capture current velocities
          const velocities = group.map(w => w.velocity);

          // Circularly assign next velocity
          for (let i = 0; i < group.length; i++) {
            group[i].velocity = velocities[(i + 1) % group.length];
          }
        }
      }
    }

    // Build return values
    // Sort walkers by id ascending
    const walkersSorted = currentWalkers
      .map(w => this._copyWalker(w)) // ensure objects are plain and detached
      .sort((a, b) => a.id - b.id);

    // Position counts
    const positionCounts = new Array(trackSize).fill(0);
    for (let i = 0; i < walkersSorted.length; i++) {
      positionCounts[walkersSorted[i].position]++;
    }

    // Aggregate velocity statistics
    let totalVelocity = 0;
    let maxVelocity = null;
    let minVelocity = null;

    if (walkersSorted.length > 0) {
      maxVelocity = walkersSorted[0].velocity;
      minVelocity = walkersSorted[0].velocity;
      for (let i = 0; i < walkersSorted.length; i++) {
        const v = walkersSorted[i].velocity;
        totalVelocity += v;
        if (v > maxVelocity) maxVelocity = v;
        if (v < minVelocity) minVelocity = v;
      }
    }

    const occupiedPositions = positionCounts.reduce(
      (acc, count) => acc + (count > 0 ? 1 : 0),
      0
    );

    const result = {
      walkers: walkersSorted,
      position_counts: positionCounts,
      total_velocity: totalVelocity,
      max_velocity: maxVelocity,
      min_velocity: minVelocity,
      occupied_positions: occupiedPositions
    };

    return result;
  }
}
