class Simulation {
  run(parameters) {
    const {
      width,
      height,
      weavers: initialWeavers,
      max_steps,
      state_modulus,
      value_modulus,
    } = parameters;

    const grid = Array.from({ length: height }, () => Array(width).fill(0));
    const weavers = initialWeavers.map((w, i) => ({
      id: i,
      x: w.x,
      y: w.y,
      dir: w.dir,
      state: w.state,
    }));

    const DELTAS = [
      { dx: 0, dy: -1 }, // 0: North
      { dx: 1, dy: 0 },  // 1: East
      { dx: 0, dy: 1 },  // 2: South
      { dx: -1, dy: 0 }, // 3: West
    ];

    for (let step = 0; step < max_steps; step++) {
      const landings = [];

      // Per spec, weavers are implicitly ordered by ID.
      for (const weaver of weavers) {
        const { dx, dy } = DELTAS[weaver.dir];
        weaver.x = (weaver.x + dx + width) % width;
        weaver.y = (weaver.y + dy + height) % height;
        landings.push({ id: weaver.id, x: weaver.x, y: weaver.y });
      }

      for (const landing of landings) {
        const weaver = weavers[landing.id];
        const { x, y } = landing;

        const oldCellValue = grid[y][x];
        const newCellValue = (oldCellValue + weaver.state) % value_modulus;
        grid[y][x] = newCellValue;

        const interactionValue = newCellValue;
        weaver.dir = (weaver.dir + interactionValue) % 4;
