// VALIDATOR IMPLEMENTATION FOR: Chrono-Weavers

class Simulation {
  constructor() {
    // No global state needed; everything lives inside run
  }

  // Helper to create 2D grid filled with zeros
  _createGrid(height, width) {
    const grid = new Array(height);
    for (let y = 0; y < height; y++) {
      const row = new Array(width);
      for (let x = 0; x < width; x++) {
        row[x] = 0;
      }
      grid[y] = row;
    }
    return grid;
  }

  run(parameters) {
    // Extract parameters
    const width = parameters.width | 0;
    const height = parameters.height | 0;
    const max_steps = parameters.max_steps | 0;
    const initial_weavers = parameters.weavers || [];
    const state_modulus = parameters.state_modulus | 0;
    const value_modulus = parameters.value_modulus | 0;

    // Initialize grid
    const grid = this._createGrid(height, width);

    // Initialize weavers with unique ids
    const weavers = new Array(initial_weavers.length);
    for (let i = 0; i < initial_weavers.length; i++) {
      const w = initial_weavers[i];
      weavers[i] = {
        id: i,
        x: w.x | 0,
        y: w.y | 0,
        dir: w.dir | 0,
        state: w.state | 0,
      };
    }

    // Direction deltas for N, E, S, W
    const DELTAS = [
      [0, -1],  // North
      [1,  0],  // East
      [0,  1],  // South
      [-1, 0],  // West
    ];

    // Main simulation loop
    for (let step = 0; step < max_steps; step++) {
      // Movement Phase
      const landings = new Array(weavers.length);
      for (let i = 0; i < weavers.length; i++) {
        const weaver = weavers[i];
        const delta = DELTAS[weaver.dir & 3]; // ensure dir within 0-3
        let newX = weaver.x + delta[0];
        let newY = weaver.y + delta[1];

        // Toroidal wrap
        newX = (newX + width) % width;
        newY = (newY + height) % height;

        weaver.x = newX;
        weaver.y = newY;

        landings[i] = { id: weaver.id, x: newX, y: newY };
      }

      // Interaction Phase
      for (let i = 0; i < landings.length; i++) {
        const landing = landings[i];
        const weaver = weavers[landing.id];
        const x = landing.x;
        const y = landing.y;

        // Update cell value
        const oldCell = grid[y][x];
        const newCell = (oldCell + weaver.state) % value_modulus;
        grid[y][x] = newCell;

        const interaction_value = newCell;

        // Update weaver dir and state
        weaver.dir = (weaver.dir + interaction_value) % 4;
        weaver.state = (weaver.state + interaction_value) % state_modulus;
      }
    }

    // Return final state
    return {
      weavers: weavers,
      grid: grid
    };
  }
}
