    // Basic validation
    if (!parameters || !Array.isArray(parameters.grid) || parameters.grid.length === 0) {
      throw new Error('Invalid grid');
    }

    const H = parameters.grid.length;
    const W = parameters.grid[0].length;

    // Validate uniform row lengths
    for (let i = 0; i < H; i++) {
      if (parameters.grid[i].length !== W) {
        throw new Error('All grid rows must have identical length');
      }
    }

    const startRow = parameters.startRow;
    const startCol = parameters.startCol;
    const startDir = parameters.startDir;
    const steps    = parameters.steps;

    // Validate directions and indices
    if (![0, 1, 2, 3].includes(startDir)) {
      throw new Error('startDir must be 0,1,2,3');
    }
    if (startRow < 0 || startRow >= H || startCol < 0 || startCol >= W) {
      throw new Error('startRow/startCol out of bounds');
    }
    if (!Number.isInteger(steps) || steps < 0) {
      throw new Error('steps must be a non-negative integer');
    }

    // Mutable copies / state
    const grid = this._copyGrid(parameters.grid);  // char matrix
    const visitCounts = Array.from({ length: H }, () => Array(W).fill(0));
    const visitLog = [];

    let r = startRow;
    let c = startCol;
    let d = startDir;

    // Direction reflection lookup tables
    const reflSlash     = [1, 0, 3, 2]; // '/'
    const reflBackSlash = [3, 2, 1, 0]; // '\'

    for (let s = 0; s < steps; s++) {
      // Move
      if (d === 0) {
        r = (r - 1 + H) % H;
      } else if (d === 1) {
        c = (c + 1) % W;
      } else if (d === 2) {
        r = (r + 1) % H;
      } else { // d === 3
        c = (c - 1 + W) % W;
      }

      // Record visit
      visitCounts[r][c] += 1;
      visitLog.push({ row: r, col: c });

      const cell = grid[r][c];

      if (cell === '/') {
        // Reflect
        d = reflSlash[d];
        // Toggle mirror
        grid[r][c] = '\\';
      } else if (cell === '\\') {
        d = reflBackSlash[d];
        grid[r][c] = '/';
      }
      // '.' does nothing
    }

    // Convert grid back to array<string>
    const finalGrid = grid.map(rowArr => rowArr.join(''));

    return {
      finalGrid,
      photon: { row: r, col: c, dir: d },
      visitCounts,
      visitLog
    };
  }
