// VALIDATOR IMPLEMENTATION FOR: Chaotic Conduit

class Simulation {
  constructor() {
    // No global state needed; pure instance per run
  }

  // Helper to compute positive modulo 256
  _mod256(n) {
    // n is always non-negative (spec guarantees), simple %
    return n & 0xff; // bitwise faster and ensures 0255
  }

  // Helper to determine direction according to specification
  _determineDirection(particleValue, particlePosition, conduitLength) {
    // Edge case: conduit length 1
    if (conduitLength === 1) {
      return (particleValue % 2 === 0) ? 1 : -1;
    }

    if (particlePosition === 0) {
      return 1;
    } else if (particlePosition === conduitLength - 1) {
      return -1;
    } else {
      return (particleValue % 2 === 0) ? 1 : -1;
    }
  }

  run(parameters) {
    // Extract parameters with defaults (spec does not mention defaults,
    // so assume they are provided correctly)
    const conduitInitial = parameters.conduit_charges || [];
    const initialParticleValue = parameters.initial_particle_value || 0;
    const maxSteps = parameters.max_steps || 0;

    // Deep copy of conduit charges (1-level array)
    const conduit = conduitInitial.slice();

    // Initialise particle and step counter
    const particle = {
      value: this._mod256(initialParticleValue),
      position: 0
    };
    let total_steps_taken = 0;
    const conduitLength = conduit.length;

    // If conduit is empty, nothing can happen; still respect maxSteps == 0 edge case
    if (conduitLength === 0 || maxSteps === 0) {
      return {
        finalParticle: { value: particle.value, position: particle.position },
        finalConduit: conduit
      };
    }

    // Ensure all initial node charges are within 0-255
    for (let i = 0; i < conduitLength; i++) {
      conduit[i] = this._mod256(conduit[i]);
    }

    // Main simulation loop
    while (total_steps_taken < maxSteps) {
      // 1. Determine Direction
      const direction = this._determineDirection(
        particle.value,
        particle.position,
        conduitLength
      );

      // 2. Interaction (Current Node)
      const currentIndex = particle.position;
      const oldParticleValue = particle.value;
      const oldNodeCharge = conduit[currentIndex];

      particle.value = this._mod256(oldParticleValue + oldNodeCharge);
      conduit[currentIndex] = this._mod256(oldNodeCharge + oldParticleValue);

      // 3. Movement
      if (conduitLength > 1) {
        particle.position = particle.position + direction;
      } else {
        // Length 1 edge case: particle stays at position 0
        particle.position = 0;
      }

      // 4. Increment Step Counter
      total_steps_taken += 1;
    }

    // Return final state
    return {
      finalParticle: {
        value: particle.value,
        position: particle.position
      },
      finalConduit: conduit
    };
  }
}
