class Simulation {
  constructor() {
    // No internal mutable state needed for deterministic behaviour
  }

  // Helper to perform a single simulation step
  _step(current, modulus) {
    const N = current.length;
    const next = new Array(N);
    for (let i = 0; i < N; i++) {
      const prev = current[(i - 1 + N) % N];
      const curr = current[i];
      const nxt  = current[(i + 1) % N];
      next[i] = (3 * curr + 2 * prev + nxt) % modulus;
    }
    return next;
  }

  run(parameters) {
    const initial_state = Array.isArray(parameters.initial_state) ? parameters.initial_state : [];
    const modulus       = parameters.modulus;
    const steps         = parameters.steps >>> 0; // ensure non-negative integer

    const N = initial_state.length;

    // Early handling for empty ring
    if (N === 0) {
      return {
        final_state: [],
        sum: 0,
        max_value: null,
        max_positions: [],
        step_count: steps
      };
    }

    // Copy to avoid mutating user-supplied array
    let current = initial_state.slice();

    for (let s = 0; s < steps; s++) {
      current = this._step(current, modulus);
    }

    // Compute summary information
    let sum = 0;
    let max_value = current[0];
    for (let i = 0; i < N; i++) {
      const val = current[i];
      sum += val;
      if (val > max_value) max_value = val;
    }

    const max_positions = [];
    for (let i = 0; i < N; i++) {
      if (current[i] === max_value) max_positions.push(i);
    }

    return {
      final_state: current,
      sum: sum,
      max_value: max_value,
      max_positions: max_positions,
      step_count: steps
    };
  }
}
