          size = rows * cols;
    const mapDir = { U: 0, R: 1, D: 2, L: 3 },
          mapChr = ['U', 'R', 'D', 'L'];
    // --- build rotor array ---
    const rotors = new Array(size);
    const src = p.rotors;
    for (let i = 0; i < size; i++) rotors[i] = mapDir[src[i]];
    // --- visits ---
    const visits = new Array(size).fill(0);
    let r = p.startRow | 0,
        c = p.startCol | 0;
    visits[r * cols + c] = 1;
    const dR = [-1, 0, 1, 0],
          dC = [0, 1, 0, -1];
    // --- main loop ---
    for (let s = 0; s < steps; s++) {
      const idx = r * cols + c;
      rotors[idx] = (rotors[idx] + 1) & 3; // clockwise rotate
      r = (r + dR[rotors[idx]] + rows) % rows;
      c = (c + dC[rotors[idx]] + cols) % cols;
      visits[r * cols + c]++;
    }
    // --- build output ---
    let rotorStr = '';
    for (let i = 0; i < size; i++) rotorStr += mapChr[rotors[i]];
    return { rotors: rotorStr, chip: { row: r, col: c }, visits };
  }
}

