    const rotorStr  = parameters.rotors;

    // Build rotor 2-D array from row-major string
    const rotors = new Array(rows);
    let idx = 0;
    for (let r = 0; r < rows; r++) {
      const rowArr = new Array(cols);
      for (let c = 0; c < cols; c++) {
        rowArr[c] = this._charToDir(rotorStr[idx++]);
      }
      rotors[r] = rowArr;
    }

    // Visits 2-D array initialised to 0
    const visits = new Array(rows);
    for (let r = 0; r < rows; r++) {
      const rowArr = new Array(cols);
      for (let c = 0; c < cols; c++) rowArr[c] = 0;
      visits[r] = rowArr;
    }

    // Place chip and register first visit
    let chipRow = startRow;
    let chipCol = startCol;
    visits[chipRow][chipCol] = 1;

    // Main loop: execute exactly `steps` iterations
    for (let s = 0; s < steps; s++) {
      // 1. rotate rotor clockwise
      rotors[chipRow][chipCol] = (rotors[chipRow][chipCol] + 1) & 3;

      // 2. move chip in new rotor direction
      const dir = rotors[chipRow][chipCol];
      if (dir === 0) {
        chipRow = (chipRow - 1 + rows) % rows;           // Up
      } else if (dir === 1) {
        chipCol = (chipCol + 1) % cols;                  // Right
      } else if (dir === 2) {
        chipRow = (chipRow + 1) % rows;                  // Down
      } else { // dir === 3
        chipCol = (chipCol - 1 + cols) % cols;           // Left
      }

      // 3. record visit
      visits[chipRow][chipCol] += 1;
    }

    // Encode final rotors back into row-major string
    let finalRotorStr = '';
    for (let r = 0; r < rows; r++) {
      for (let c = 0; c < cols; c++) {
        finalRotorStr += this._dirToChar(rotors[r][c]);
      }
    }

    // Flatten visits row-major
    const flatVisits = new Array(rows * cols);
    idx = 0;
    for (let r = 0; r < rows; r++) {
      for (let c = 0; c < cols; c++) {
        flatVisits[idx++] = visits[r][c];
      }
    }

    // Assemble and return snapshot object
    return {
      rotors: finalRotorStr,
      chip: { row: chipRow, col: chipCol },
      visits: flatVisits
    };
  }
}
