    const ring = parameters.ring;
    const max_steps = parameters.max_steps;

    // Initialize cells from ring string
    const N = ring.length;
    let cells = [];
    for (let i = 0; i < N; i++) {
      cells[i] = parseInt(ring[i]);
    }

    // Simulate for max_steps
    for (let step = 0; step < max_steps; step++) {
      const d = (step % 3) + 1;
      const new_cells = [...cells];

      for (let i = 0; i < N; i++) {
        const left_idx = (i - d + N) % N;
        const right_idx = (i + d) % N;
        new_cells[i] = cells[left_idx] ^ cells[right_idx];

        if (i % 3 === 0) {
          const opposite_idx = (i + Math.floor(N / 2)) % N;
          new_cells[i] = new_cells[i] ^ cells[opposite_idx];
        }
      }

      cells = new_cells;
    }

    // Calculate return values
    let sum = 0;
    let pattern_hash = 0;
    let run_length = 0;
    let current_run = 0;
    let alternations = 0;

    for (let i = 0; i < N; i++) {
      sum += cells[i];
      pattern_hash += cells[i] * (i + 1);

      if (cells[i] === 1) {
        current_run++;
        run_length = Math.max(run_length, current_run);
      } else {
        current_run = 0;
      }

      const next_i = (i + 1) % N;
      if (cells[i] !== cells[next_i]) {
        alternations++;
      }
    }

    return {
      cells: cells,
      sum: sum,
      pattern_hash: pattern_hash,
      run_length: run_length,
      alternations: alternations
    };
  }
