// VALIDATOR IMPLEMENTATION FOR: XOR Cascade Network

class Simulation {
  constructor() {
    // No mutable global state required
  }

  // -------- Helper: mathematical modulo that is always non-negative --------
  static _mod(a, n) {
    return ((a % n) + n) % n;
  }

  // -------- Helper: compute derived statistics for the return schema --------
  static _computeStats(cells) {
    const N = cells.length;
    let sum = 0;
    let patternHash = 0;
    let runLength = 0;
    let currentRun = 0;
    let alternations = 0;

    for (let i = 0; i < N; i++) {
      const value = cells[i];

      // sum
      sum += value;

      // pattern hash
      patternHash += value * (i + 1);

      // longest linear run of 1s (no wrap-around)
      if (value === 1) {
        currentRun += 1;
        if (currentRun > runLength) runLength = currentRun;
      } else {
        currentRun = 0;
      }

      // alternations with wrap-around
      const nextValue = cells[(i + 1) % N];
      if (value !== nextValue) alternations += 1;
    }

    return { sum, patternHash, runLength, alternations };
  }

  // -------- Core simulation as per specification --------
  run(parameters) {
    // -------- Input validation and initialisation --------
    if (
      !parameters ||
      typeof parameters.ring !== "string" ||
      parameters.ring.length === 0
    ) {
      throw new Error("Parameter 'ring' must be a non-empty binary string.");
    }

    const ringStr = parameters.ring;
    const cells = [];

    for (let ch of ringStr) {
      if (ch !== "0" && ch !== "1") {
        throw new Error("Ring string contains non-binary character.");
      }
      cells.push(ch === "1" ? 1 : 0);
    }

    const N = cells.length;
    let stepCount = 0;

    let maxSteps = parameters.max_steps;
    if (
      maxSteps === undefined ||
      maxSteps === null ||
      typeof maxSteps !== "number" ||
      !Number.isInteger(maxSteps) ||
      maxSteps < 0
    ) {
      throw new Error("Parameter 'max_steps' must be a non-negative integer.");
    }

    // Early exit if no steps requested
    if (maxSteps === 0) {
      const stats = Simulation._computeStats(cells);
      return {
        cells,
        sum: stats.sum,
        pattern_hash: stats.patternHash,
        run_length: stats.runLength,
        alternations: stats.alternations,
      };
    }

    // -------- Simulation loop --------
    let currentCells = cells;

    for (let s = 0; s < maxSteps; s++, stepCount++) {
      const d = (stepCount % 3) + 1; // distance
      const newCells = currentCells.slice(); // copy

      for (let i = 0; i < N; i++) {
        const leftIdx = Simulation._mod(i - d, N);
        const rightIdx = Simulation._mod(i + d, N);

        // XOR with neighbours at distance d
        newCells[i] = currentCells[leftIdx] ^ currentCells[rightIdx];

        // Additional XOR every third cell (i mod 3 == 0)
        if (i % 3 === 0) {
          const oppositeIdx =
            Simulation._mod(i + Math.floor(N / 2), N);
          newCells[i] = newCells[i] ^ currentCells[oppositeIdx];
        }
      }

      currentCells = newCells;
    }

    // -------- Produce final statistics --------
    const stats = Simulation._computeStats(currentCells);

    return {
      cells: currentCells,
      sum: stats.sum,
      pattern_hash: stats.patternHash,
      run_length: stats.runLength,
      alternations: stats.alternations,
    };
  }
}
