class Simulation {
  constructor() {}

  run(parameters) {
    const { particles, ring_size, max_steps } = parameters;

    // Initialize particle state
    let state = particles.map((p, index) => ({
      id: index,
      position: p.position % ring_size,
      velocity: p.velocity
    }));

    for (let step = 0; step < max_steps; step++) {
      // Phase 1: Move all particles
      for (let particle of state) {
        particle.position = (particle.position + particle.velocity) % ring_size;
        if (particle.position < 0) {
          particle.position += ring_size;
        }
      }

      // Phase 2: Handle collisions at each position
      const position_groups = {};
      for (let particle of state) {
        if (!position_groups[particle.position]) {
          position_groups[particle.position] = [];
        }
        position_groups[particle.position].push(particle);
      }

      // Process collisions deterministically
      for (let pos in position_groups) {
        const group = position_groups[pos];
        if (group.length > 1) {
          // Sort by ID for deterministic order
          group.sort((a, b) => a.id - b.id);

          // Collision rule: velocities rotate cyclically
          const velocities = group.map(p => p.velocity);
          for (let i = 0; i < group.length; i++) {
            group[i].velocity = velocities[(i + 1) % group.length];
          }
        }
      }
    }

    // Compute return values
    const total_kinetic_energy = state.reduce((sum, p) => sum + p.velocity * p.velocity, 0);
    const position_sum = state.reduce((sum, p) => sum + p.position, 0);

    return {
      particles: state.map(p => ({ id: p.id, position: p.position, velocity: p.velocity })),
      total_kinetic_energy: total_kinetic_energy,
      position_sum: position_sum,
      step_count: max_steps
    };
  }
}

