    // Extract parameters with defaults safeguarded by spec assumptions
    const inputParticles = Array.isArray(parameters.particles) ? parameters.particles : [];
    const ringSize       = parameters.ring_size;
    const maxSteps       = parameters.max_steps >>> 0; // force non-negative integer

