    for (let i = 0; i < inputParticles.length; i++) {
      const p = inputParticles[i] || {};
      const position = this._mod(p.position || 0, ringSize);
      const velocity = (p.velocity || 0);
      state.push({ id: i, position, velocity });
    }

    // Main simulation loop
    for (let step = 0; step < maxSteps; step++) {
      // Movement phase
      for (let i = 0; i < state.length; i++) {
        const particle = state[i];
        particle.position = this._mod(particle.position + particle.velocity, ringSize);
      }

      // Collision phase
      const positionGroups = Object.create(null);
      for (let i = 0; i < state.length; i++) {
        const particle = state[i];
        const posKey   = particle.position; // integer key is fine
        if (!positionGroups[posKey]) positionGroups[posKey] = [];
        positionGroups[posKey].push(particle);
      }

      for (const pos in positionGroups) {
        const group = positionGroups[pos];
        if (group.length > 1) {
          // Sort by id ascending
          group.sort((a, b) => a.id - b.id);

          // Capture current velocities
          const velocities = new Array(group.length);
          for (let i = 0; i < group.length; i++) velocities[i] = group[i].velocity;

          // Cyclically assign velocities
          const lastIdx = group.length - 1;
          for (let i = 0; i < group.length; i++) {
            group[i].velocity = velocities[(i + 1) % group.length];
          }
        }
      }
    }

    // Compute statistics
    let totalKineticEnergy = 0;
    let positionSum        = 0;
    for (let i = 0; i < state.length; i++) {
      const { position, velocity } = state[i];
      totalKineticEnergy += velocity * velocity; // integer arithmetic
      positionSum        += position;
    }

    // Prepare return object
    const resultParticles = state.map(p => ({
      id: p.id,
      position: p.position,
      velocity: p.velocity
    }));

    return {
      particles: resultParticles,
      total_kinetic_energy: totalKineticEnergy,
      position_sum: positionSum,
      step_count: maxSteps
    };
