        giver.energy -= transfer_amount
        receiver.energy += transfer_amount

        // Type Flip
        mover.type = 1 - mover.type
        target_collider.type = 1 - target_collider.type
      else:
        // --- Movement ---
        delete position_map[mover.position]
        mover.position = target_pos
        position_map[target_pos] = mover

