// VALIDATOR IMPLEMENTATION FOR: Chrono-Colliders

class Simulation {
  constructor() {
    // No mutable globals; all state kept inside run
  }

  // Helper: deep copy simple JSON-serialisable objects
  _clone(obj) {
    return JSON.parse(JSON.stringify(obj));
  }

  // Helper: builds a position map { position -> colliderObject }
  _buildPositionMap(colliders) {
    const map = Object.create(null);
    for (const col of colliders) {
      map[col.position] = col;
    }
    return map;
  }

  // Helper: determines giver / receiver for energy transfer
  _determineGiver(mover, target) {
    if (mover.energy > target.energy) return { giver: mover, receiver: target };
    if (target.energy > mover.energy) return { giver: target, receiver: mover };
    // energies equal  lower id gives
    return mover.id < target.id
      ? { giver: mover, receiver: target }
      : { giver: target, receiver: mover };
  }

  run(parameters) {
    // Defensive cloning so caller data is untouched
    const params = this._clone(parameters || {});

    // Extract and validate parameters
    const gridSize = params.grid_size;
    if (!Number.isInteger(gridSize) || gridSize <= 0) {
      throw new Error("grid_size must be a positive integer");
    }

    let maxSteps = params.max_steps;
    if (!Number.isInteger(maxSteps)) maxSteps = 0;
    if (maxSteps < 0) maxSteps = 0;

    const initial = Array.isArray(params.initial_colliders)
      ? params.initial_colliders
      : [];

    // Initialise colliders
    const colliders = [];
    const occupied = new Set();
    for (let i = 0; i < initial.length; i++) {
      const ic = initial[i] || {};
      const pos = ic.pos;
      const typ = ic.type;
      const en = ic.energy;

      if (
        !Number.isInteger(pos) ||
        pos < 0 ||
        pos >= gridSize ||
        (typ !== 0 && typ !== 1) ||
        !Number.isInteger(en) ||
        en < 0
      ) {
        throw new Error("Invalid collider initialisation data");
      }
      if (occupied.has(pos)) {
        throw new Error("Two colliders cannot start at the same position");
      }
      occupied.add(pos);

      colliders.push({
        id: i,
        position: pos,
        type: typ,
        energy: en,
        total_collisions: 0,
      });
    }

    // Simulation loop
    for (let step = 0; step < maxSteps; step++) {
      // 1. Determine processing order
      const processingOrder = colliders
        .map((c) => c.id)
        .sort((aId, bId) => {
          const a = colliders[aId];
          const b = colliders[bId];
          if (a.position !== b.position) return a.position - b.position;
          return a.id - b.id;
        });

      // 2. Build position map
      const positionMap = this._buildPositionMap(colliders);

      // 3. Process each collider
      for (const moverId of processingOrder) {
        const mover = colliders[moverId];

        // Re-calculate move distance each time (energy may change)
        const moveDist = mover.energy % gridSize;

        // Target position
        let targetPos;
        if (mover.type === 0) {
          targetPos = (mover.position + moveDist) % gridSize;
        } else {
          targetPos = (mover.position - moveDist + gridSize) % gridSize;
        }

        // If no movement, continue
        if (targetPos === mover.position) continue;

        const targetCollider = positionMap[targetPos];

        if (targetCollider && targetCollider.id !== mover.id) {
          // --- Collision ---
          mover.total_collisions += 1;
          targetCollider.total_collisions += 1;

          // Energy transfer
          const { giver, receiver } = this._determineGiver(
            mover,
            targetCollider
          );
          const transferAmount = Math.floor(giver.energy / 2);
          giver.energy -= transferAmount;
          receiver.energy += transferAmount;

          // Type flip
          mover.type = 1 - mover.type;
          targetCollider.type = 1 - targetCollider.type;
          // Positions remain unchanged; positionMap remains valid
        } else {
          // --- Successful movement ---
          delete positionMap[mover.position];
          mover.position = targetPos;
          positionMap[targetPos] = mover;
        }
      }
    }

    // Prepare output
    const result = {
      parameters: this._clone(parameters),
      final_step: maxSteps,
      colliders: colliders
        .slice()
        .sort((a, b) => a.id - b.id)
        .map((c) => ({
          id: c.id,
          position: c.position,
          type: c.type,
          energy: c.energy,
          total_collisions: c.total_collisions,
        })),
    };

    return result;
  }
}
