        { r: (pr - 1 + this.height) % this.height, c: pc }, // UP
        { r: pr, c: (pc - 1 + this.width) % this.width },    // LEFT
        { r: pr, c: pc },                                  // CENTER
        { r: pr, c: (pc + 1) % this.width },    // RIGHT
        { r: (pr + 1) % this.height, c: pc }               // DOWN
