// VALIDATOR IMPLEMENTATION FOR: Crystalline Resonators

class Simulation {
  constructor() {
    /* Stateless implementation */
  }

  /* -------- Helper to parse the initial grid string -------- */
  _parseGrid(str, width, height) {
    /* 1. Try to extract all integer tokens (handles multi-digit with delimiters) */
    let tokens = str.match(/\d+/g) || [];
    if (tokens.length !== width * height) {
      /* 2. Fallback: treat every single digit character as one value */
      tokens = Array.from(str)
        .filter(ch => /\d/.test(ch))
        .map(ch => ch);
    }
    if (tokens.length !== width * height) {
      throw new Error('initial_grid_str does not contain the correct amount of numbers');
    }
    const grid = [];
    let idx = 0;
    for (let r = 0; r < height; r++) {
      const row = [];
      for (let c = 0; c < width; c++) {
        row.push(Number(tokens[idx++]));
      }
      grid.push(row);
    }
    return grid;
  }

  /* -------- Helper to wrap coordinates toroidally -------- */
  _wrap(val, size) {
    /* JavaScript % keeps sign; ensure positive result */
    const res = val % size;
    return res < 0 ? res + size : res;
  }

  /* -------- Helper to build final_grid_str -------- */
  _serializeGrid(grid) {
    /* Concatenate every number directly (row-major) */
    return grid.map(row => row.join('')).join('');
  }

  run(parameters) {
    /* ----- Extract parameters ----- */
    const width = parameters.width;
    const height = parameters.height;
    const modulus = parameters.modulus;
    const max_steps = parameters.max_steps;
    const initial_grid_str = parameters.initial_grid_str;
    const pulse_pos_input = parameters.initial_pulse_pos;

    /* ----- Initialize grid and pulse ----- */
    const grid = this._parseGrid(initial_grid_str, width, height);
    let pulse_pos = { r: pulse_pos_input.r, c: pulse_pos_input.c };

    /* ----- Early termination if max_steps === 0 ----- */
    if (max_steps === 0) {
      const final_grid_str0 = this._serializeGrid(grid);
      const sum0 = grid.flat().reduce((a, b) => a + b, 0);
      return {
        final_grid_str: final_grid_str0,
        final_pulse_pos: { r: pulse_pos.r, c: pulse_pos.c },
        sum_of_energies: sum0
      };
    }

    /* ----- Simulation loop ----- */
    for (let step = 0; step < max_steps; step++) {
      const pr = pulse_pos.r;
      const pc = pulse_pos.c;

      /* Neighbor positions to update in order: UP, DOWN, LEFT, RIGHT */
      const neighborsToUpdate = [
        { r: this._wrap(pr - 1, height), c: pc },        // UP
        { r: this._wrap(pr + 1, height), c: pc },        // DOWN
        { r: pr,                             c: this._wrap(pc - 1, width) }, // LEFT
        { r: pr,                             c: this._wrap(pc + 1, width) }  // RIGHT
      ];

      /* Update each neighbor sequentially */
      for (const nPos of neighborsToUpdate) {
        const nr = nPos.r;
        const nc = nPos.c;

        const n_up_r    = this._wrap(nr - 1, height);
        const n_down_r  = this._wrap(nr + 1, height);
        const n_left_c  = this._wrap(nc - 1, width);
        const n_right_c = this._wrap(nc + 1, width);

        const S_neighbor =
          grid[n_up_r][nc] +
          grid[n_down_r][nc] +
          grid[nr][n_left_c] +
          grid[nr][n_right_c];

        grid[nr][nc] = (grid[nr][nc] + S_neighbor) % modulus;
      }

      /* Determine next pulse position: candidates in order UP, LEFT, CENTER, RIGHT, DOWN */
      const moveCandidates = [
        { r: this._wrap(pr - 1, height), c: pc },                 // UP
        { r: pr,                         c: this._wrap(pc - 1, width) }, // LEFT
        { r: pr,                         c: pc },                 // CENTER
        { r: pr,                         c: this._wrap(pc + 1, width) }, // RIGHT
        { r: this._wrap(pr + 1, height), c: pc }                  // DOWN
      ];

      let max_energy = -1;
      let next_pulse_pos = moveCandidates[0]; // default (will be replaced on >)

      for (const cand of moveCandidates) {
        const energy = grid[cand.r][cand.c];
        if (energy > max_energy) {
          max_energy = energy;
          next_pulse_pos = cand;
        }
      }

      pulse_pos = { r: next_pulse_pos.r, c: next_pulse_pos.c };
    }

    /* ----- Compose final result ----- */
    const final_grid_str = this._serializeGrid(grid);
    const sum_of_energies = grid.flat().reduce((a, b) => a + b, 0);

    return {
      final_grid_str,
      final_pulse_pos: { r: pulse_pos.r, c: pulse_pos.c },
      sum_of_energies
    };
  }
}
