class Simulation {
  constructor() {}

  run(parameters) {
    const { walkers: walker_specs, ring_size, max_steps } = parameters;

    let walkers = walker_specs.map(spec => ({
      position: ((spec.position % ring_size) + ring_size) % ring_size,
      step_size: spec.step_size,
      charge: spec.charge
    }));

    let current_step = 0;

    for (let step = 0; step < max_steps; step++) {
      // Movement phase
      for (let i = 0; i < walkers.length; i++) {
        walkers[i].position = ((walkers[i].position + walkers[i].step_size) % ring_size + ring_size) % ring_size;
      }

      // Interaction phase
      const position_map = new Map();
      for (let i = 0; i < walkers.length; i++) {
        const pos = walkers[i].position;
        if (!position_map.has(pos)) {
          position_map.set(pos, []);
        }
        position_map.get(pos).push({ walker: walkers[i], original_index: i });
      }

      for (const [pos, group] of position_map) {
        if (group.length >= 2) {
          // Sort by charge (ascending), then by original index
          group.sort((a, b) => {
            if (a.walker.charge !== b.walker.charge) {
              return a.walker.charge - b.walker.charge;
            }
            return a.original_index - b.original_index;
          });

          // Apply pairwise interactions
          for (let i = 0; i < group.length - 1; i++) {
            const w1 = group[i].walker;
            const w2 = group[i + 1].walker;
            const temp1 = (w1.step_size + w2.charge) % 11;
            const temp2 = (w2.step_size + w1.charge) % 11;
            w1.step_size = temp1;
            w2.step_size = temp2;
          }
        }
      }

      current_step = step + 1;
    }

    const position_sum = walkers.reduce((sum, w) => sum + w.position, 0);
    const step_size_sum = walkers.reduce((sum, w) => sum + w.step_size, 0);

    return {
      walkers: walkers.map(w => ({...w})),
      ring_size: ring_size,
      current_step: current_step,
      position_sum: position_sum,
      step_size_sum: step_size_sum
    };
  }
}

