// VALIDATOR IMPLEMENTATION FOR: Modular Ring Walkers

class Simulation {
  constructor() {
    // No mutable globals  all state is inside run()
  }

  // Helper: mathematical modulo that always returns 0  m-1
  _mod(n, m) {
    return ((n % m) + m) % m;
  }

  run(parameters) {
    const { walkers = [], ring_size, max_steps = 0 } = parameters;

    // Deep-copy walkers and attach immutable original indices
    const simWalkers = walkers.map((w, idx) => ({
      position: this._mod(w.position, ring_size),
      step_size: w.step_size,
      charge: w.charge,
      __index: idx            // internal only, stripped from result
    }));

    let current_step = 0;

    // Main simulation loop
    while (current_step < max_steps) {
      /* 1. Movement Phase */
      for (let i = 0; i < simWalkers.length; i++) {
        const w = simWalkers[i];
        w.position = this._mod(w.position + w.step_size, ring_size);
      }

      /* 2. Interaction Phase */
      const groups = new Map(); // position  array of walkers

      // Group walkers by position
      for (let i = 0; i < simWalkers.length; i++) {
        const w = simWalkers[i];
        if (!groups.has(w.position)) groups.set(w.position, []);
        groups.get(w.position).push(w);
      }

      // Resolve interactions at each occupied position
      groups.forEach(group => {
        if (group.length < 2) return; // no collision

        // Sort by charge asc, then original index asc
        group.sort((a, b) => {
          if (a.charge !== b.charge) return a.charge - b.charge;
          return a.__index - b.__index;
        });

        // Pairwise adjacent interactions
        for (let i = 0; i < group.length - 1; i++) {
          const w1 = group[i];
          const w2 = group[i + 1];

          const new_step1 = this._mod(w1.step_size + w2.charge, 11);
          const new_step2 = this._mod(w2.step_size + w1.charge, 11);

          w1.step_size = new_step1;
          w2.step_size = new_step2;
        }
      });

      current_step++;
    }

    // Prepare result, stripping internal fields
    const resultWalkers = simWalkers.map(w => ({
      position: w.position,
      step_size: w.step_size,
      charge: w.charge
    }));

    // Aggregate sums
    let position_sum = 0;
    let step_size_sum = 0;
    for (let i = 0; i < simWalkers.length; i++) {
      position_sum += simWalkers[i].position;
      step_size_sum += simWalkers[i].step_size;
    }

    // Return final state per schema
    return {
      walkers: resultWalkers,
      ring_size: ring_size,
      current_step: current_step,
      position_sum: position_sum,
      step_size_sum: step_size_sum
    };
  }
}
