  grid_size = parameters.grid_size
  max_steps = parameters.max_steps
  bots = initialize_bots(parameters.initial_bots)
  step = 0

  while step < max_steps AND count(bots) > 1:
    next_positions = new Map()
    surviving_bot_ids = new Set(all bot_ids from bots)

    // Phase 1: Plan Moves
    sorted_bots = sort bots by ascending id
    for bot in sorted_bots:
      (nx, ny, ndir) = calculate_move(bot, grid_size)
      if is_off_grid(nx, ny, grid_size):
        surviving_bot_ids.delete(bot.id)
      else:
        key = format("%d,%d", nx, ny)
        if not next_positions.has(key):
          next_positions.set(key, [])
        next_positions.get(key).append(bot.id)

    // Phase 2: Resolve Collisions
    for key in next_positions.keys():
      id_list = next_positions.get(key)
      if count(id_list) > 1:
        for bot_id in id_list:
          surviving_bot_ids.delete(bot_id)

    // Phase 3: Update State
    new_bots = []
    for bot in sorted_bots:
      if surviving_bot_ids.has(bot.id):
        (nx, ny, ndir) = calculate_move(bot, grid_size)
        new_bot = bot
        new_bot.x = nx
        new_bot.y = ny
        new_bot.dir = ndir
        new_bot.prog_idx = (bot.prog_idx + 1) % length(bot.program)
        new_bots.append(new_bot)

