function getAnswer(parameters) {
  const sim = new Simulation();
  const initial_bot_ids = new Set(parameters.initial_bots.map((_, i) => i));
  let total = 0;
  let bots_left = new Set(initial_bot_ids);

  for (let s = 1; s <= parameters.max_steps; s++) {
    const current_params = { ...parameters, max_steps: s };
    const result = sim.run(current_params);
    const remaining_ids = new Set(result.bots.map(b => b.id));

    const annihilated_this_step = new Set([...bots_left].filter(id => !remaining_ids.has(id)));

    if (annihilated_this_step.size > 0) {
        for (const id of annihilated_this_step) {
            total += id + result.step;
        }
    }

    bots_left = remaining_ids;
    if (bots_left.size <= 1) break;
  }

  return total;
}

