class Simulation {
  _bots = [];
  _grid_size = [0, 0];
  _step = 0;
  _max_steps = 0;

  _DIRS = { N: 0, E: 1, S: 2, W: 3 };
  _VECTORS = [
    { x: 0, y: -1 }, // N
    { x: 1, y: 0 },  // E
    { x: 0, y: 1 },  // S
    { x: -1, y: 0 }, // W
  ];

  _init(parameters) {
    this._grid_size = parameters.grid_size;
    this._max_steps = parameters.max_steps;
    this._step = 0;
    this._bots = parameters.initial_bots.map((p, i) => ({
      id: i,
      x: p[0],
      y: p[1],
      dir: this._DIRS[p[2]],
      program: p[3],
      prog_idx: 0,
    }));
  }

  _calculateMove(bot) {
    const instruction = bot.program[bot.prog_idx];
    let nx = bot.x, ny = bot.y, ndir = bot.dir;

    if (instruction === 'F') {
      const vec = this._VECTORS[bot.dir];
      nx += vec.x;
      ny += vec.y;
    } else if (instruction === 'R') {
      ndir = (bot.dir + 1) % 4;
    } else if (instruction === 'L') {
      ndir = (bot.dir + 3) % 4;
    }
    return { nx, ny, ndir };
  }

  run(parameters) {
    this._init(parameters);

    while (this._step < this._max_steps && this._bots.length > 1) {
      const next_positions = new Map();
      const surviving_bot_ids = new Set(this._bots.map(b => b.id));

      // Phase 1: Plan Moves
      // Bots are already sorted by ID implicitly from initialization
      for (const bot of this._bots) {
        const { nx, ny } = this._calculateMove(bot);

        if (nx < 0 || nx >= this._grid_size[0] || ny < 0 || ny >= this._grid_size[1]) {
          surviving_bot_ids.delete(bot.id);
        } else {
          const key = `${nx},${ny}`;
          if (!next_positions.has(key)) {
            next_positions.set(key, []);
          }
          next_positions.get(key).push(bot.id);
        }
      }

      // Phase 2: Resolve Collisions
      for (const ids of next_positions.values()) {
        if (ids.length > 1) {
          for (const id of ids) {
            surviving_bot_ids.delete(id);
          }
        }
      }

      // Phase 3: Update State
      const new_bots = [];
      for (const bot of this._bots) {
        if (surviving_bot_ids.has(bot.id)) {
          const { nx, ny, ndir } = this._calculateMove(bot);
          new_bots.push({
            ...bot,
            x: nx,
            y: ny,
            dir: ndir,
            prog_idx: (bot.prog_idx + 1) % bot.program.length,
          });
        }
      }

      this._bots = new_bots;
      this._step++;
    }

    return { step: this._step, bots: this._bots };
  }
}

