// VALIDATOR IMPLEMENTATION FOR: Gridbots

class Simulation {
  constructor() {
    // The Simulation class is stateless between run() calls
  }

  // Helper: convert direction character to numeric code
  _dirCharToNum(c) {
    switch (c) {
      case 'N': return 0;
      case 'E': return 1;
      case 'S': return 2;
      case 'W': return 3;
      default:
        throw new Error(`Invalid direction character: ${c}`);
    }
  }

  // Helper: compute next position and direction for a bot
  _calculateMove(bot) {
    const instruction = bot.program[bot.prog_idx];
    if (instruction === 'L') {
      const ndir = (bot.dir + 3) % 4;
      return { nx: bot.x, ny: bot.y, ndir };
    }
    if (instruction === 'R') {
      const ndir = (bot.dir + 1) % 4;
      return { nx: bot.x, ny: bot.y, ndir };
    }
    if (instruction === 'F') {
      const dx = [0, 1, 0, -1];
      const dy = [-1, 0, 1, 0];
      const ndir = bot.dir;
      const nx = bot.x + dx[ndir];
      const ny = bot.y + dy[ndir];
      return { nx, ny, ndir };
    }
    throw new Error(`Invalid instruction character: ${instruction}`);
  }

  run(parameters) {
    // Extract parameters
    const gridSize = parameters.grid_size;
    const width = gridSize[0];
    const height = gridSize[1];
    const maxSteps = parameters.max_steps;

    const initialBots = parameters.initial_bots || [];

    // Initialize bots array
    const bots = [];
    for (let i = 0; i < initialBots.length; i++) {
      const tuple = initialBots[i];
      const program = tuple[3];
      if (!program || program.length === 0) {
        throw new Error('Program string must have at least one character');
      }
      bots.push({
        id: i,
        x: tuple[0],
        y: tuple[1],
        dir: this._dirCharToNum(tuple[2]),
        program: program,
        prog_idx: 0
      });
    }

    // Edge case: max_steps === 0
    if (maxSteps === 0) {
      return { step: 0, bots: bots.slice() };
    }

    // Edge case: no initial bots
    if (bots.length === 0) {
      return { step: maxSteps, bots: [] };
    }

    let step = 0;
    let currentBots = bots.slice(); // make a shallow copy to avoid modifying the original array

    // Main simulation loop
    while (step < maxSteps && currentBots.length > 1) {
      const nextPositions = new Map();               // key -> array of bot ids
      const survivingBotIds = new Set();             // start with all ids
      const sortedBots = currentBots.slice().sort((a, b) => a.id - b.id);

      // Initially mark all bots as surviving
      for (const bot of sortedBots) {
        survivingBotIds.add(bot.id);
      }

      // Phase 1: Plan Moves
      for (const bot of sortedBots) {
        const { nx, ny } = this._calculateMove(bot);

        // Off-grid check
        if (nx < 0 || nx >= width || ny < 0 || ny >= height) {
          survivingBotIds.delete(bot.id);
        } else {
          const key = `${nx},${ny}`;
          if (!nextPositions.has(key)) {
            nextPositions.set(key, []);
          }
          nextPositions.get(key).push(bot.id);
        }
      }

      // Phase 2: Resolve Collisions
      for (const [/*key*/, idList] of nextPositions) {
        if (idList.length > 1) {
          for (const bid of idList) {
            survivingBotIds.delete(bid);
          }
        }
      }

      // Phase 3: Update State
      const newBots = [];
      for (const bot of sortedBots) {
        if (survivingBotIds.has(bot.id)) {
          const move = this._calculateMove(bot);
          const updatedBot = {
            id: bot.id,
            x: move.nx,
            y: move.ny,
            dir: move.ndir,
            program: bot.program,
            prog_idx: (bot.prog_idx + 1) % bot.program.length
          };
          newBots.push(updatedBot);
        }
      }

      currentBots = newBots;
      step += 1;
    }

    // Return final state
    return {
      step: step,
      bots: currentBots
    };
  }
}
