class Simulation {
  constructor() {
    // No internal mutable state required
  }

  // Helper: return 0-9 for digit characters, else 0
  _digitValue(ch) {
    const code = ch.charCodeAt(0);
    return (code >= 48 && code <= 57) ? (code - 48) : 0;
  }

  run(parameters) {
    // Safely extract parameters
    const seedStr = String(
      parameters && Object.prototype.hasOwnProperty.call(parameters, "seed")
        ? parameters.seed
        : ""
    );

    const rawModulus =
      typeof parameters?.modulus === "number" && Number.isFinite(parameters.modulus)
        ? Math.trunc(parameters.modulus)
        : 2;
    const M = rawModulus >= 2 ? rawModulus : 2;

    const rawSteps =
      typeof parameters?.steps === "number" && Number.isFinite(parameters.steps)
        ? Math.trunc(parameters.steps)
        : 0;
    const steps = rawSteps >= 0 ? rawSteps : 0;

    const N = seedStr.length;

    // Edge case: empty seed
    if (N === 0) {
      return { cells: [], step_count: 0 };
    }

    // Initialize cells array
    const cells = new Array(N);
    for (let i = 0; i < N; i++) {
      cells[i] = this._digitValue(seedStr.charAt(i)) % M;
    }

    // Evolution steps
    let current = cells;
    for (let t = 0; t < steps; t++) {
      const nextCells = new Array(N);
      for (let i = 0; i < N; i++) {
        const prev = current[(i - 1 + N) % N];
        const curr = current[i];
        const nxt = current[(i + 1) % N];
        nextCells[i] = (2 * prev + 3 * curr + nxt) % M;
      }
      current = nextCells;
    }

    return { cells: current, step_count: steps };
  }
}
