class Simulation {
    constructor() {
        this.size = 0;
        this.frogs = [];
        this.grid = [];
        this.max_steps = 0;
        this.step_count = 0;
        this.total_interactions = 0;
    }

    _initState(params) {
        this.size = params.size;
        this.max_steps = params.max_steps;

        this.frogs = params.initial_frogs.map((f, i) => ({
            id: i,
            pos: f.pos,
            spin: f.spin,
            dir: f.dir,
        }));

        this.frogs.sort((a, b) => a.id - b.id);

        this.step_count = 0;
        this.total_interactions = 0;
        this._rebuildGrid();
    }

    _rebuildGrid() {
        this.grid = new Array(this.size).fill(null);
        for (const frog of this.frogs) {
            if (frog.pos >= 0 && frog.pos < this.size) {
                this.grid[frog.pos] = frog;
            }
        }
    }

    _getFrogById(id) {
        return this.frogs[id];
    }

    _step() {
        const interaction_pairs = new Set();
        const targets = {};

        for (const frog of this.frogs) {
            const dest = (frog.pos + frog.dir + this.size) % this.size;
            const target_frog = this.grid[dest];

            if (target_frog) {
                const id1 = Math.min(frog.id, target_frog.id);
                const id2 = Math.max(frog.id, target_frog.id);
                interaction_pairs.add(`${id1}-${id2}`);
            } else {
                if (!targets[dest]) {
                    targets[dest] = [];
                }
                targets[dest].push(frog.id);
            }
        }

        const pending_updates = [];
        const frogs_in_interaction = new Set();

        for (const pair_str of interaction_pairs) {
            this.total_interactions++;
            const [id1_str, id2_str] = pair_str.split('-');
            const id_a = parseInt(id1_str, 10);
            const id_b = parseInt(id2_str, 10);

            frogs_in_interaction.add(id_a);
            frogs_in_interaction.add(id_b);

            const frog_a = this._getFrogById(id_a);
            const frog_b = this._getFrogById(id_b);

            const new_spin_a = (frog_a.spin ^ frog_b.spin) + 1;
            const new_spin_b = (frog_a.spin & frog_b.spin) << 1;

            pending_updates.push({
                type: 'interact',
                id_a: id_a,
                id_b: id_b,
                new_spin_a: new_spin_a,
            });
        }

        for (const dest in targets) {
            const candidates = targets[dest].filter(id => !frogs_in_interaction.has(id));
            if (candidates.length === 0) continue;

            let winner_id = (candidates.length === 1) ? candidates[0] : Math.min(...candidates);

            pending_updates.push({
                type: 'move',
                id: winner_id,
                new_pos: parseInt(dest, 10)
            });
        }

        for (const update of pending_updates) {
            if (update.type === 'move') {
                this._getFrogById(update.id).pos = update.new_pos;
            } else if (update.type === 'interact') {
                const frog_a = this._getFrogById(update.id_a);
                const frog_b = this._getFrogById(update.id_b);
                const new_spin_a = (frog_a.spin ^ frog_b.spin) + 1;
                const new_spin_b = (frog_a.spin & frog_b.spin) << 1;
                frog_a.spin = new_spin_a;
                frog_b.spin = new_spin_b;
                frog_a.dir *= -1;
                frog_b.dir *= -1;
            }
        }

        this._rebuildGrid();
