// VALIDATOR IMPLEMENTATION FOR: Quantum Frogs on a Ring

class Simulation {
  constructor() {
    // no global mutable state outside run
  }

  // --- helper to rebuild spatial grid ---
  _rebuildGrid(size, frogs) {
    const grid = new Array(size).fill(null);
    // ascending id order means higher ids overwrite, leaving highest id occupant
    for (let i = 0; i < frogs.length; i++) {
      const frog = frogs[i];
      grid[frog.pos] = frog;
    }
    return grid;
  }

  // --- run simulation according to spec ---
  run(parameters) {
    // ----- Initialization -----
    const size = parameters.size >>> 0;              // ensure non-negative integer
    const maxSteps = parameters.max_steps >>> 0;
    const initialFrogs = Array.isArray(parameters.initial_frogs)
      ? parameters.initial_frogs
      : [];

    // create frogs with ids
    const frogs = [];
    for (let i = 0; i < initialFrogs.length; i++) {
      const spec = initialFrogs[i];
      frogs.push({
        id: i,
        pos: ((spec.pos % size) + size) % size,      // wrap into [0,size)
        spin: spec.spin >>> 0,                       // store as unsigned 32-bit int
        dir: spec.dir === -1 ? -1 : 1                // only -1 or 1
      });
    }

    // sort by id (already ascending but ensure)
    frogs.sort((a, b) => a.id - b.id);

    // initial grid
    let grid = this._rebuildGrid(size, frogs);

    // mapping id -> frog (never changes reference)
    const frogById = Object.create(null);
    for (let i = 0; i < frogs.length; i++) {
      frogById[frogs[i].id] = frogs[i];
    }

    // state vars
    let stepCount = 0;
    let totalInteractions = 0;

    // early termination for zero steps
    if (maxSteps === 0) {
      return {
        final_frogs: frogs.map(f => ({
          id: f.id,
          pos: f.pos,
          spin: f.spin,
          dir: f.dir
        })),
        total_interactions: totalInteractions,
        total_steps: stepCount
      };
    }

    // ----- main loop -----
    while (stepCount < maxSteps) {
      // Phase 1: Planning
      const interactionPairs = new Set();             // strings "idA-idB"
      const targets = Object.create(null);            // dest -> array of ids

      for (let i = 0; i < frogs.length; i++) {
        const frog = frogs[i];
        const dest = (frog.pos + frog.dir + size) % size;
        const targetFrog = grid[dest];

        if (targetFrog !== null) {
          const idA = frog.id < targetFrog.id ? frog.id : targetFrog.id;
          const idB = frog.id < targetFrog.id ? targetFrog.id : frog.id;
          interactionPairs.add(idA + '-' + idB);
        } else {
          if (!targets[dest]) targets[dest] = [];
          targets[dest].push(frog.id);
        }
      }

      // Phase 2: Resolution
      const pendingUpdates = [];
      const frogsInInteraction = new Set();

      // Resolve interactions
      interactionPairs.forEach(pairKey => {
        totalInteractions += 1;
        const parts = pairKey.split('-');
        const idA = Number(parts[0]);
        const idB = Number(parts[1]);
        frogsInInteraction.add(idA);
        frogsInInteraction.add(idB);

        const frogA = frogById[idA];
        const frogB = frogById[idB];

        const newSpinA = ((frogA.spin ^ frogB.spin) >>> 0) + 1;
        const newSpinB = ((frogA.spin & frogB.spin) << 1) >>> 0;

        pendingUpdates.push({
          type: 'interact',
          ids: [idA, idB],
          spins: [newSpinA >>> 0, newSpinB >>> 0]
        });
      });

      // Resolve moves
      for (const destStr in targets) {
        const dest = Number(destStr);
        const candidates = targets[dest];
        const valid = [];
        for (let i = 0; i < candidates.length; i++) {
          const cid = candidates[i];
          if (!frogsInInteraction.has(cid)) valid.push(cid);
        }
        if (valid.length === 0) continue;
        let winner = valid[0];
        for (let i = 1; i < valid.length; i++) {
          if (valid[i] < winner) winner = valid[i];
        }
        pendingUpdates.push({
          type: 'move',
          id: winner,
          pos: dest
        });
      }

      // Phase 3: Application
      for (let i = 0; i < pendingUpdates.length; i++) {
        const upd = pendingUpdates[i];
        if (upd.type === 'interact') {
          const idA = upd.ids[0];
          const idB = upd.ids[1];
          const frogA = frogById[idA];
          const frogB = frogById[idB];
          frogA.spin = upd.spins[0] >>> 0;
          frogB.spin = upd.spins[1] >>> 0;
          frogA.dir = -frogA.dir;
          frogB.dir = -frogB.dir;
        } else if (upd.type === 'move') {
          const frog = frogById[upd.id];
          frog.pos = upd.pos;
        }
      }

      // rebuild grid after applying all updates
      grid = this._rebuildGrid(size, frogs);

      // increment step count
      stepCount += 1;
    }

    // ----- Prepare result -----
    const finalFrogs = frogs.map(f => ({
      id: f.id,
      pos: f.pos,
      spin: f.spin,
      dir: f.dir
    }));

    return {
      final_frogs: finalFrogs,
      total_interactions: totalInteractions,
      total_steps: stepCount
    };
  }
}
