class Simulation {
  constructor() {
    // No persistent state needed between runs
  }

  // Helper: convert rotor int to character
  _dirToChar(dir) {
    return dir === 0 ? 'N' : dir === 1 ? 'E' : dir === 2 ? 'S' : 'W'; // 3 -> 'W'
  }

