    let chipRow = parameters.startRow;
    let chipCol = parameters.startCol;
    const steps = parameters.steps;

    // Initialise grids
    const rotorGrid = new Array(rows);  // stores ints 0-3
    const visitGrid = new Array(rows);  // stores ints 0

    for (let r = 0; r < rows; r++) {
      rotorGrid[r] = new Array(cols);
      visitGrid[r] = new Array(cols);
      const rowString = initialRotors[r];
      for (let c = 0; c < cols; c++) {
        rotorGrid[r][c] = this._charToDir(rowString[c]);
        visitGrid[r][c] = 0;
      }
    }

    // Starting cell visited once
    visitGrid[chipRow][chipCol] = 1;

    // Direction deltas for N,E,S,W
    const dRows = [-1, 0, 1, 0];
    const dCols = [0, 1, 0, -1];

    // Main simulation loop
    for (let t = 1; t <= steps; t++) {
      // 1. current rotor dir
      let dir = rotorGrid[chipRow][chipCol];

      // 2. rotate clockwise
      dir = (dir + 1) & 3; // mod 4

      // 3. write back
      rotorGrid[chipRow][chipCol] = dir;

      // 4. move according to new dir
      chipRow = (chipRow + dRows[dir] + rows) % rows;
      chipCol = (chipCol + dCols[dir] + cols) % cols;

      // 5. increment visits
      visitGrid[chipRow][chipCol] += 1;
    }

    // Construct rotors as strings
    const rotorStrings = new Array(rows);
    let uniqueVisited = 0;
    let maxVisits = 0;
    let minVisits = Number.MAX_SAFE_INTEGER;

    for (let r = 0; r < rows; r++) {
      let str = '';
      for (let c = 0; c < cols; c++) {
        // Rotor char
        str += this._dirToChar(rotorGrid[r][c]);

        // Stats for visits
        const v = visitGrid[r][c];
        if (v > 0) uniqueVisited += 1;
        if (v > maxVisits) maxVisits = v;
        if (v < minVisits) minVisits = v;
      }
      rotorStrings[r] = str;
    }

    // Assemble result per schema
    return {
      finalPosition: { row: chipRow, col: chipCol },
      rotors: rotorStrings,
      visits: visitGrid,
      uniqueVisited: uniqueVisited,
      maxVisits: maxVisits,
      minVisits: minVisits
    };
  }
}
