class Simulation {
  constructor() {
    this.cells = [];
    this.N = 0;
    this.M = 0;
    this.P = 0;
    this.step = 0;
  }

  _init(params) {
    this.N = params.N || 0;
    this.M = params.M || 1;
    this.P = params.P || 2;
    this.max_steps = params.max_steps || 0;

    this.cells = new Array(this.N);
    for (let i = 0; i < this.N; i++) {
      this.cells[i] = {
        value: params.initial_values[i],
        chirality: params.initial_chiralities[i],
      };
    }
    this.step = 0;
  }

  _step() {
    if (this.N === 0) return;

    const next_cells = new Array(this.N);

    for (let i = 0; i < this.N; i++) {
      const current_cell = this.cells[i];
      const source_idx = (i + current_cell.chirality + this.N) % this.N;
      const source_cell = this.cells[source_idx];

      const new_value = (current_cell.value + source_cell.value) % this.M;
      let new_chirality = current_cell.chirality;

      if (new_value % this.P === 0) {
        new_chirality = -current_cell.chirality;
      }

      next_cells[i] = { value: new_value, chirality: new_chirality };
    }

    this.cells = next_cells;
    this.step++;
  }

  run(params) {
    this._init(params);

    for (let i = 0; i < this.max_steps; i++) {
      this._step();
    }

    // Return a deep copy to prevent external mutation of the final state
    const final_cells = this.cells.map(c => ({ ...c }));

    return {
      step: this.step,
      cells: final_cells,
    };
  }
}

