class Simulation {
  constructor() {
    // No global mutable state required
  }

  // Helper to return the mathematical sign as defined in the spec
  _sign(n) {
    if (n > 0) return 1;
    if (n < 0) return -1;
    return 0;
  }

  // Deepcopy the planets list without mutating the original input
  _clonePlanets(inputPlanets) {
    const cloned = new Array(inputPlanets.length);
    for (let i = 0; i < inputPlanets.length; i++) {
      const p = inputPlanets[i];
      cloned[i] = {
        id: i,               // will be overwritten later just to be explicit
        x:  p.x | 0,
        y:  p.y | 0,
        vx: p.vx | 0,
        vy: p.vy | 0,
        charge: p.charge | 0
      };
    }
    return cloned;
  }

  // Locate initial max-charge record (lowest id on ties)
  _initialMaxChargeRecord(planets) {
    if (planets.length === 0) {
      return { planet_id: -1, charge: 0 };
    }
    let maxCharge = planets[0].charge;
    let planetId  = planets[0].id;
    for (let i = 1; i < planets.length; i++) {
      const ch = planets[i].charge;
      if (ch > maxCharge) {
        maxCharge = ch;
        planetId  = planets[i].id;
      }
    }
    return { planet_id: planetId, charge: maxCharge };
  }

  run(parameters) {
    // 1. Clone planets and assign ids
    const planets = this._clonePlanets(parameters.planets || []);
    for (let i = 0; i < planets.length; i++) {
      planets[i].id = i;
    }

    // 2. Extract other params
    const max_steps      = parameters.max_steps | 0;
    const warp_threshold = parameters.warp_threshold | 0;

    // 3. Aggregate state
    let step              = 0;
    let total_warps       = 0;
    let total_resonances  = 0;
    let max_charge_record = this._initialMaxChargeRecord(planets);

    // Edge case: 0 steps -> return immediately
    if (max_steps === 0) {
      return {
        step,
        planets,
        total_warps,
        total_resonances,
        max_charge_record
      };
    }

    // MAIN SIMULATION LOOP
    for (step = 1; step <= max_steps; step++) {
      // 1. Gravity
      for (let i = 0; i < planets.length; i++) {
        const p = planets[i];
        p.vx -= this._sign(p.x);
        p.vy -= this._sign(p.y);
        p.x  += p.vx;
        p.y  += p.vy;
      }

      // 2. Resonance
      // Build coordinate groups
      const coordMap = Object.create(null); // { "x,y": [planet,...] }
      for (let i = 0; i < planets.length; i++) {
        const p = planets[i];
        const key = p.x + ',' + p.y;
        if (coordMap[key] === undefined) coordMap[key] = [];
        coordMap[key].push(p);
      }

      const sortedKeys = Object.keys(coordMap).sort();
      for (let k = 0; k < sortedKeys.length; k++) {
        const key = sortedKeys[k];
        const group = coordMap[key];
        if (group.length > 1) {
          total_resonances++;

          // total charge
          let total_charge = 0;
          let minId = group[0].id;
          for (let i = 0; i < group.length; i++) {
            const g = group[i];
            total_charge += g.charge;
            if (g.id < minId) minId = g.id;
          }

          // Assign consolidated charge
          for (let i = 0; i < group.length; i++) {
            const g = group[i];
            if (g.id === minId) {
              g.charge = total_charge;
            } else {
              g.charge = 0;
            }
          }

          // Update max charge record if strictly greater
          if (total_charge > max_charge_record.charge) {
            max_charge_record = { planet_id: minId, charge: total_charge };
          }
        }
      }

      // 3. Warping
      for (let i = 0; i < planets.length; i++) {
        const p = planets[i];
        if (p.charge >= warp_threshold) {
          total_warps++;
          p.x += p.vx;
          p.y += p.vy;
          p.charge = p.charge % warp_threshold;
        }
      }
    }

    // Return final state
    return {
      step: max_steps,
      planets,
      total_warps,
      total_resonances,
      max_charge_record
    };
  }
