class Simulation {
    _loom_size;
    _MODULUS;
    _weavers;
    _loom;
    _step;

    constructor() {}

    _init(parameters) {
        this._loom_size = parameters.loom_size;
        this._MODULUS = parameters.MODULUS;
        this._weavers = JSON.parse(JSON.stringify(parameters.initial_weavers));
        this._weavers.sort((a, b) => a.id - b.id);
        this._loom = new Array(this._loom_size).fill(0);
        this._step = 0;
    }

    run(parameters) {
        this._init(parameters);
        const max_steps = parameters.max_steps;

        for (this._step = 0; this._step < max_steps; this._step++) {
            this._run_step();
        }

        return {
            loom: this._loom,
            weavers: JSON.parse(JSON.stringify(this._weavers)),
            step: this._step
        };
    }

    _run_step() {
        // Phase 1: Modify Loom
        for (const weaver of this._weavers) {
            this._loom[weaver.pos] = (this._loom[weaver.pos] + weaver.key) % this._MODULUS;
        }

        // Phase 2: Plan Movement
        const targets = new Map();
        for (const weaver of this._weavers) {
            const target_pos = (weaver.pos + weaver.dir + this._loom_size) % this._loom_size;
            if (!targets.has(target_pos)) {
                targets.set(target_pos, []);
            }
            targets.get(target_pos).push(weaver.id);
        }

        // Phase 3: Identify and Resolve Conflicts
        const conflicted_weaver_ids = new Set();
        const sorted_target_keys = Array.from(targets.keys()).sort((a, b) => a - b);

        for (const target_pos of sorted_target_keys) {
            const weaver_ids = targets.get(target_pos);
            if (weaver_ids.length > 1) {
                const min_id_in_group = Math.min(...weaver_ids);
                weaver_ids.sort((a, b) => a - b);

                for (const id of weaver_ids) {
                    conflicted_weaver_ids.add(id);
                    const weaver_to_update = this._weavers.find(w => w.id === id);
                    if (weaver_to_update) {
                        weaver_to_update.dir *= -1;
                        weaver_to_update.key = (weaver_to_update.key + min_id_in_group) % this._MODULUS;
                    }
                }
            }
        }

        // Phase 4: Execute Movement
        for (const weaver of this._weavers) {
            if (!conflicted_weaver_ids.has(weaver.id)) {
                weaver.pos = (weaver.pos + weaver.dir + this._loom_size) % this._loom_size;
            }
