class Simulation {
  constructor() {
    // No persistent state needed across runs
  }

  // Helper: safe modulus ensuring non-negative result
  _mod(a, n) {
    return ((a % n) + n) % n;
  }

  // Deep copy utility using JSON methods (inputs contain only numbers)
  _deepCopy(obj) {
    return JSON.parse(JSON.stringify(obj));
  }

  run(parameters) {
    // Parameter extraction
    const loom_size  = parameters.loom_size;
    const MODULUS    = parameters.MODULUS;
    const max_steps  = parameters.max_steps;
    const initial_weavers = parameters.initial_weavers || [];

    // Initialisation
    const weavers = this._deepCopy(initial_weavers).sort((a, b) => a.id - b.id);
    const loom = new Array(loom_size).fill(0);
    let step = 0;

    // Convenience: map id -> weaver object (references inside `weavers` array)
    const idToWeaver = new Map();
    for (const w of weavers) {
      idToWeaver.set(w.id, w);
    }

    // Main simulation loop
    for (step = 0; step < max_steps; step++) {

      // Phase 1: Modification
      for (const w of weavers) {
        const idx = w.pos;
        loom[idx] = this._mod(loom[idx] + w.key, MODULUS);
      }

      // Phase 2: Movement Planning
      const targets = new Map(); // key: target_pos, value: array of ids
      for (const w of weavers) {
        const target_pos = this._mod(w.pos + w.dir, loom_size);
        if (!targets.has(target_pos)) targets.set(target_pos, []);
        targets.get(target_pos).push(w.id);
      }

      // Phase 3: Conflict Resolution
      const conflicted_weaver_ids = new Set();
      const sortedTargetPositions = Array.from(targets.keys()).sort((a, b) => a - b);

      for (const target_pos of sortedTargetPositions) {
        const idsAtTarget = targets.get(target_pos);
        if (idsAtTarget.length > 1) {
          const min_id_in_group = Math.min(...idsAtTarget);
          idsAtTarget.sort((a, b) => a - b); // deterministic order
          for (const id of idsAtTarget) {
            conflicted_weaver_ids.add(id);
            const w = idToWeaver.get(id);
            w.dir *= -1;
            w.key = this._mod(w.key + min_id_in_group, MODULUS);
          }
        }
      }

      // Phase 4: Movement Execution
      for (const w of weavers) {
        if (!conflicted_weaver_ids.has(w.id)) {
          w.pos = this._mod(w.pos + w.dir, loom_size);
        }
      }
    }

    // Return final state
    return {
      loom: loom,
      weavers: weavers,
      step: max_steps
