class Simulation {
  constructor() {
    this.width = 0;
    this.height = 0;
    this.bots = [];
  }

  _modulo(a, n) {
    return ((a % n) + n) % n;
  }

  _initialize(params) {
    this.width = params.width;
    this.height = params.height;
    this.bots = params.bots.map((bot, index) => ({
      id: index,
      x: bot.x,
      y: bot.y,
      vx: bot.vx,
      vy: bot.vy,
      charge: bot.charge,
    }));
  }

  run(parameters) {
    this._initialize(parameters);
    const max_steps = parameters.max_steps;

    for (let step = 0; step < max_steps; step++) {
      // Phase 1: Movement
      for (const bot of this.bots) {
        bot.x = this._modulo(bot.x + bot.vx, this.width);
        bot.y = this._modulo(bot.y + bot.vy, this.height);
      }

      // Phase 2: Interaction
      const cellMap = new Map();
      for (const bot of this.bots) {
        const key = `${bot.x},${bot.y}`;
        if (!cellMap.has(key)) {
          cellMap.set(key, []);
        }
        cellMap.get(key).push(bot.id);
      }

      const interactionKeys = [];
      for (const [key, occupants] of cellMap) {
        if (occupants.length > 1) {
          interactionKeys.push(key);
        }
      }
      interactionKeys.sort();

      const newVelocities = new Map();

      for (const key of interactionKeys) {
        const botIdsInCell = cellMap.get(key);
        const occupants = botIdsInCell.map(id => this.bots[id]);
        // The bots are already sorted by id because of how we get them from this.bots

        const k = occupants.length;
        let sum_vx = 0;
        let sum_vy = 0;
        for (const bot of occupants) {
          sum_vx += bot.vx;
          sum_vy += bot.vy;
        }

        const avg_vx = Math.floor(sum_vx / k);
        const avg_vy = Math.floor(sum_vy / k);

        const [x_cell_str, y_cell_str] = key.split(',');
        const x_cell = parseInt(x_cell_str, 10);
        const y_cell = parseInt(y_cell_str, 10);

        for (const bot of occupants) {
          const new_vx = avg_vx + (bot.charge % 3) - 1;
          const new_vy = avg_vy + (this._modulo(bot.charge + x_cell + y_cell, 3)) - 1;
          newVelocities.set(bot.id, { vx: new_vx, vy: new_vy });
        }
      }

      if (newVelocities.size > 0) {
        for (const bot of this.bots) {
          if (newVelocities.has(bot.id)) {
            const update = newVelocities.get(bot.id);
            bot.vx = update.vx;
            bot.vy = update.vy;
          }
        }
      }
    }

    // Return a deep copy of the final state bots, sorted by ID for consistency.
    const final_bots = this.bots.map(b => ({...b})).sort((a, b) => a.id - b.id);
    return { bots: final_bots };
  }
}

