// VALIDATOR IMPLEMENTATION FOR: Resonant Orbits

class Simulation {
  constructor() {
    // No persistent state required between runs
  }

  // Positive modulo to handle negative values correctly
  _mod(n, m) {
    return ((n % m) + m) % m;
  }

  // Deep-clone the input bot list and assign IDs
  _initializeBots(botsInput) {
    const bots = [];
    for (let i = 0; i < botsInput.length; i++) {
      const b = botsInput[i];
      bots.push({
        id: i,
        x: b.x,
        y: b.y,
        vx: b.vx,
        vy: b.vy,
        charge: b.charge
      });
    }
    return bots;
  }

  run(parameters) {
    const width  = parameters.width;
    const height = parameters.height;
    const maxSteps = parameters.max_steps || 0;

    // Initialize bots with deterministic IDs
    let bots = this._initializeBots(parameters.bots || []);

    // Edge case: run zero steps
    if (maxSteps === 0 || bots.length === 0) {
      return { bots };
    }

    // Ensure deterministic order by id (ascending)
    bots.sort((a, b) => a.id - b.id);

    for (let step = 1; step <= maxSteps; step++) {

      /* Phase 1: Movement */
      for (let i = 0; i < bots.length; i++) {
        const bot = bots[i];
        bot.x = this._mod(bot.x + bot.vx, width);
        bot.y = this._mod(bot.y + bot.vy, height);
      }

      /* Phase 2: Interaction */
      const cellMap = new Map(); // key -> array of bots

      for (let i = 0; i < bots.length; i++) {
        const bot = bots[i];
        const key = `${bot.x},${bot.y}`;
        let arr = cellMap.get(key);
        if (arr === undefined) {
          arr = [];
          cellMap.set(key, arr);
        }
        arr.push(bot);
      }

      // Gather interaction cells
      const interactionKeys = [];
      for (const [key, occupants] of cellMap.entries()) {
        if (occupants.length > 1) {
          interactionKeys.push(key);
        }
      }

      interactionKeys.sort(); // lexicographical ascending order

      const newVelocities = new Map(); // bot.id -> {vx, vy}

      for (let kIndex = 0; kIndex < interactionKeys.length; kIndex++) {
        const key = interactionKeys[kIndex];
        const occupants = cellMap.get(key).slice(); // copy
        // Sort occupants by id ascending
        occupants.sort((a, b) => a.id - b.id);

        const k = occupants.length;
        let sumVx = 0;
        let sumVy = 0;
        for (let j = 0; j < k; j++) {
          sumVx += occupants[j].vx;
          sumVy += occupants[j].vy;
        }

        const avgVx = Math.floor(sumVx / k);
        const avgVy = Math.floor(sumVy / k);

        const [xCellStr, yCellStr] = key.split(',');
        const xCell = parseInt(xCellStr, 10);
        const yCell = parseInt(yCellStr, 10);

        for (let j = 0; j < k; j++) {
          const bot = occupants[j];
          const newVx = avgVx + (bot.charge % 3) - 1;
          const newVy = avgVy + ((bot.charge + xCell + yCell) % 3) - 1;
          newVelocities.set(bot.id, { vx: newVx, vy: newVy });
        }
      }

      // Apply velocity updates simultaneously
      for (let i = 0; i < bots.length; i++) {
        const bot = bots[i];
        const update = newVelocities.get(bot.id);
        if (update !== undefined) {
          bot.vx = update.vx;
          bot.vy = update.vy;
        }
      }
    }

    return { bots };
  }
