// VALIDATOR IMPLEMENTATION FOR: Elemental Conduit Flow

class Simulation {
  constructor() {
    // No global mutable state; everything handled inside run
  }

  // Helper to clamp charge within [0, 1000]
  _clampCharge(value) {
    if (value < 0) return 0;
    if (value > 1000) return 1000;
    return value;
  }

  run(parameters) {
    const initialConfig = parameters.initial_config || [];
    const maxSteps = parameters.max_steps || 0;

    // Deep copy initial configuration and clamp initial charges
    const conduit = initialConfig.map(cell => ({
      charge: this._clampCharge(Math.floor(cell.charge)),
      element: cell.element
    }));

    const N = conduit.length;
    let stepCount = 0;

    for (let step = 0; step < maxSteps; step++) {
      const nextChargeDeltas = new Array(N).fill(0);
      const currentElements = conduit.map(cell => cell.element); // Snapshot of elements at phase start

      // Phase 1: Replication (Fire cells replicate to the right)
      for (let i = 0; i < N; i++) {
        const cell = conduit[i];
        if (cell.element === 'Fire' && cell.charge >= 100) {
          const replicateAmount = Math.floor(cell.charge * 0.10);
          if (i + 1 < N) {
            nextChargeDeltas[i] -= replicateAmount;
            nextChargeDeltas[i + 1] += replicateAmount;
          }
        }
      }

      // Phase 2: Absorption (Water cells absorb from non-Water neighbors)
      for (let i = 0; i < N; i++) {
        if (conduit[i].element === 'Water') {
          // From left neighbor
          if (i - 1 >= 0 && currentElements[i - 1] !== 'Water') {
            const amount = Math.floor(conduit[i - 1].charge * 0.05);
            nextChargeDeltas[i] += amount;
            nextChargeDeltas[i - 1] -= amount;
          }
          // From right neighbor
          if (i + 1 < N && currentElements[i + 1] !== 'Water') {
            const amount = Math.floor(conduit[i + 1].charge * 0.05);
            nextChargeDeltas[i] += amount;
            nextChargeDeltas[i + 1] -= amount;
          }
        }
      }

      // Phase 3: Apply charge deltas and perform element transformations
      for (let i = 0; i < N; i++) {
        conduit[i].charge = this._clampCharge(conduit[i].charge + nextChargeDeltas[i]);
      }

      // Element transformations in ascending index order
      for (let i = 0; i < N; i++) {
        const cell = conduit[i];
        if (cell.element === 'Earth' && cell.charge > 500) {
          cell.element = 'Fire';
        } else if (cell.element === 'Air' && cell.charge < 50) {
          cell.element = 'Water';
        }
      }

      stepCount += 1;
    }

    // Return deep copy of final conduit state
    return {
      conduit: conduit.map(cell => ({ charge: cell.charge, element: cell.element })),
      step_count: stepCount
    };
  }
}
