function manhattan_distance(p1, p2, width, height): // Toroidal Manhattan distance
    dx = Math.abs(p1.x - p2.x)
    dy = Math.abs(p1.y - p2.y)
    return Math.min(dx, width - dx) + Math.min(dy, height - dy)

function simulate(initial_config, max_steps):
    grid = initialize_grid(initial_config, width, height)

    for step = 0 to max_steps-1:
        prev_grid = deep_copy(grid)
        next_grid = deep_copy(grid) // Represents the state AFTER movement attempts and conduit redirection

        // Store original locations for elements that move, for conduit tie-breaking
        moved_element_mapping = {}
        movement_order_counter = 0

        // Element Movement Phase (Charge-Driven)
        active_drain = find_active_element(prev_grid, 3) // Finds {y,x} or null

        intended_moves = [] // [{from: {y,x}, to: {ny,nx}}]

        for y from 0 to height-1:
            for x from 0 to width-1:
                cell = prev_grid[y][x]
                if cell.element !== 3 && cell.element !== -1: // Not drain and not empty
                    if active_drain:
                        preferred_target = null
                        min_dist = Infinity

                        neighbors_coords = [
                            {dy: -1, dx: 0}, // Up
                            {dy: 1, dx: 0},  // Down
                            {dy: 0, dx: -1}, // Left
                            {dy: 0, dx: 1}   // Right
                        ]

                        for n_idx from 0 to 3:
                            neighbor_coord = neighbors_coords[n_idx]
                            ny = (y + neighbor_coord.dy + height) % height
                            nx = (x + neighbor_coord.dx + width) % width

                            dist = manhattan_distance({y: ny, x: nx}, active_drain, width, height)

                            if dist < min_dist:
                                min_dist = dist
                                preferred_target = {y: ny, x: nx}
                            // Implicit tie-breaker by iteration order (Up, Down, Left, Right)

                        if preferred_target:
                            target_cell_val = prev_grid[preferred_target.y][preferred_target.x].charge
                            if cell.charge > target_cell_val:
                                intended_moves.push({from: {y,x}, to: preferred_target, original_element_val: cell, order: movement_order_counter++})

        // Sort intended moves for resolution
        // Primary: from.y, then from.x
        // Secondary: to.y, then to.x
        sort(intended_moves, by 'from' (row-major), then 'to' (row-major))

        occupied_next_cells = new Set() // Store 'ny,nx' strings
        // Resolve movements
        for move in intended_moves:
            target_key = `${move.to.y},${move.to.x}`
            if !occupied_next_cells.has(target_key):
                next_grid[move.to.y][move.to.x] = prev_grid[move.from.y][move.from.x]
                next_grid[move.from.y][move.from.x] = {element: -1, charge: 0} // Empty original cell
                occupied_next_cells.add(target_key)
                moved_element_mapping[target_key] = move // Store the move for conduit tie-breaking
            else:
                // Element stays put, original cell not cleared if it was not empty
                // For elements that wanted to move and failed, their original position in next_grid remains as it was
                // However, their element value in next_grid will be copied from prev_grid, so no need to explicitly copy back
                continue // Element does not move, its state in next_grid stays as initialized from prev_grid.

        // Clear cells that moved out AFTER all resolutions
        for y from 0 to height-1:
            for x from 0 to width-1:
                if prev_grid[y][x].element !== -1 && next_grid[y][x].element === prev_grid[y][x].element && next_grid[y][x].charge === prev_grid[y][x].charge: // If cell was not empty and content is same, means original element did not move out/new element did not move in
                    // This isn't quite right. We need to explicitly track what moved. simpler is to initialize next_grid with defaults,
                    // then fill in where elements moved TO. Then fill in elements that did not move.
                    // BETTER WAY: Initialize next_grid, then iterate original_grid, if element moved, put it into target cell, and clear original cell in next_grid.
                    // If element did NOT move, copy it directly from original_grid to next_grid.
                    // This is what the code implements: `next_grid = deep_copy(grid)` at start, then overwrite `next_grid[move.to.y][move.to.x] = prev_grid[move.from.y][move.from.x]`
                    // And `next_grid[move.from.y][move.from.x] = {element: -1, charge: 0}`. This implies original cells HAVE to be cleared.
                    // The implicit assumption is that if an element *tried* to move and *failed*, its cell in `next_grid` remains its original value.
                    // The `next_grid` is initialized as a full copy of `prev_grid`. Then, if an element at (y,x) moves to (ny,nx), then:
                    // `next_grid[ny][nx] = prev_grid[y][x]`
                    // `next_grid[y][x] = {element: -1, charge: 0}`

        // Conduit Redirection Phase (Post-Movement)
        active_beacon = find_active_element(next_grid, 0) // Finds {y,x} or null

        conduit_teleports = [] // [{from: {y,x}, element_data: {element,charge}, original_move_order: int}]

        for y from 0 to height-1:
            for x from 0 to width-1:
                cell = next_grid[y][x]
                if cell.element === 4: // Is a conduit
                    // Figure out what element just moved into this conduit cell. This is complex due to `next_grid` state. 
                    // Re-scan prev_grid, find what elements came into this actual cell via `intended_moves` that succeeded.
                    // This requires storing the element data that entered the cell.
                    // For simplicity, we assume `next_grid[y][x]` *is* the element to teleport.
                    // A more robust implementation would track if an element *moved into* this conduit cell.
                    // For strict deterministic behavior, we track `moved_element_mapping`:
                    target_key = `${y},${x}`
                    if moved_element_mapping.hasOwnProperty(target_key):
                        move_info = moved_element_mapping[target_key]
                        conduit_teleports.push({from: {y,x}, element_data: move_info.original_element_val, original_move_order: move_info.order})

        // Sort conduit teleports by original_move_order, then by 'from' cell (row-major)
        sort(conduit_teleports, by 'original_move_order', then 'from' (row-major))

        if active_beacon:
            beacon_occupants = {}
            for teleport_info in conduit_teleports:
                if !beacon_occupants.hasOwnProperty(`${active_beacon.y},${active_beacon.x}`): // Only first one occupies beacon
                    next_grid[active_beacon.y][active_beacon.x] = teleport_info.element_data
                    next_grid[teleport_info.from.y][teleport_info.from.x] = {element: -1, charge: 0} // Clear conduit cell
                    beacon_occupants[`${active_beacon.y},${active_beacon.x}`] = true
                else:
                    // Element is lost, conduit cell original element is cleared, no other element occupies beacon
                    next_grid[teleport_info.from.y][teleport_info.from.x] = {element: -1, charge: 0}

        // Charge Update Phase on final `next_grid`
        for y from 0 to height-1:
            for x from 0 to width-1:
                cell = next_grid[y][x]

                if cell.element === 3: // Drain consumption
                    neighbors_coords_adj = [
                        {dy: -1, dx: 0}, {dy: 1, dx: 0}, {dy: 0, dx: -1}, {dy: 0, dx: 1}
                    ]
                    for neighbor_coord in neighbors_coords_adj:
                        ny = (y + neighbor_coord.dy + height) % height
                        nx = (x + neighbor_coord.dx + width) % width
                        n_cell = next_grid[ny][nx]
